-- NPC: npc-regina-jones
INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-regina-jones', 'Реджина Джонс — фиксёр и специалист по киберпсихозу', '{"metadata": {"id": "npc-regina-jones", "title": "Реджина Джонс — фиксёр и специалист по киберпсихозу", "document_type": "canon", "category": "narrative", "status": "draft", "version": "0.2.0", "last_updated": "2025-11-12T01:40:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["npc", "fixer", "cyberpsychosis"], "topics": ["npc-lore", "humanitarian-contracts"], "related_systems": ["narrative-service", "social-service", "health-service"], "related_documents": [{"id": "canon-lore-cyberpsychosis-program", "relation": "references"}, {"id": "npc-viktor-vektor", "relation": "collaborates_with"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/regina-jones.md", "visibility": "internal", "audience": ["narrative", "systems", "live_ops"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль Регины существовал в Markdown и не описывал связи с программами «не-смертельного» реагирования и квестами Watson.", "goal": "Создать YAML-документ, отражающий гуманитарный подход Регины, её влияние на игроков и связь с системами киберпсихоза.", "essence": "«Фиксер Watson, делающая ставку на не-смертельные решения и защиту жителей района.»", "key_points": ["Зафиксированы мотивации, страхи и отношения с фракциями.", "Добавлены игровые функции, модифицирующие репутацию и доступ к гуманитарным ресурсам.", "Определены диалоговые паттерны и эволюция доверия к игроку."]}, "content": {"sections": [{"id": "identity", "title": "Идентичность и зона влияния", "body": "- Имя: Regina Jones, ID `npc-regina-jones`.\\n- Роль: фиксёр района Watson, эксперт по кейсам киберпсихоза.\\n- Локация: офис-студия в Little China, мобильные посты наблюдения.\\n- Происхождение: бывший медиа, перешедшая в независимые фиксёры.\\n", "mechanics_links": [], "assets": []}, {"id": "appearance", "title": "Внешность и оснащение", "body": "- Стройная женщина 30–45 лет, короткая стрижка, AR-линзы.\\n- Импланты: коммуникационные колодки, аналитические модули, медицинские сенсоры.\\n- Стиль: функциональный, в серо-синих тонах, минимум украшений.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и цели", "body": "- Мотивация: защита жителей Watson, снижение уровня насилия.\\n- Цели: развитие протоколов «не-смертельного» реагирования, поддержка пострадавших.\\n- Страхи: эскалация уличного хаоса, корпоративный саботаж гуманитарных программ.\\n- Желания: честные контракты, сильная сеть помощи, сохранение репутации.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и взаимодействия", "body": "- Фракции: прагматичные контакты с NCPD, осторожные переговоры с бандами, дистанция от корпораций.\\n- NPC-партнёры: риппердоки-волонтёры, Виктор Вектор, социальные активисты.\\n- Игрок: доверие растёт при не-смертельных решениях и уважении к её кодексу.\\n", "mechanics_links": ["canon/narrative/scenarios/factions/netfactions/netwatch-voodoo-boys-2020-2093.yaml"], "assets": []}, {"id": "gameplay_hooks", "title": "Игровые функции и бонусы", "body": "- Narrative-service: выдаёт линейки кейсов киберпсихоза, расследования Watson.\\n- Social-service: снижает штрафы за мирные решения, открывает доступ к консультантам.\\n- Health-service: активирует программы реабилитации и специальные расходники.\\n- Навыки: высокие социальные показатели, средние аналитические и технические способности.\\n", "mechanics_links": [], "assets": []}, {"id": "dialogue", "title": "Диалог и тон", "body": "- Приветствия: «Работа найдётся. Но давай без крови», «С тобой надёжно. У меня есть сложный кейс».\\n- Темы разговоров: этика контрактов, статистика киберпсихоза, безопасность граждан.\\n- В ходе миссий предоставляет дайджесты, по итогам благодарит за спасённые жизни.\\n", "mechanics_links": [], "assets": []}, {"id": "arc", "title": "Арка развития", "body": "- Старт: фиксёр-гуманист, ведущий Watson через диалог.\\n- Развитие: построение сети реабилитации, конфликты с радикалами и корпорациями.\\n- Финалы: запуск фонда помощи, срыв программ из-за корпоративного давления, нейтральная стагнация.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Cyberpsychosis Case Files", "link": "canon/narrative/npc-lore/cyberpsychosis-index.yaml"}, {"title": "quests.json Mapping", "link": "canon/narrative/npc-lore/quests-index.yaml"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.2.0", "date": "2025-11-12", "author": "narrative_team", "changes": "Конверсия профиля Реджины Джонс в YAML и добавление гуманитарных игровых функций."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

