INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('canon-npc-netrunner-spider-murphy-2020-2077', 'Spider Murphy — легендарный netrunner', '{"metadata": {"id": "canon-npc-netrunner-spider-murphy-2020-2077", "title": "Spider Murphy — легендарный netrunner", "document_type": "canon", "category": "narrative", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-06T11:50:00+00:00", "owners": [{"role": "npc_lead", "contact": "npc@necp.game"}], "tags": ["npc", "netrunner", "legend"], "topics": ["narrative", "netrunning"], "related_systems": ["npc-service"], "related_documents": [{"id": "canon-scenarios-tech-net-history-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/netrunners/spider-murphy-legend-2020-2077.md", "visibility": "internal", "audience": ["narrative", "gameplay"], "risk_level": "high"}, "review": {"chain": [{"role": "npc_lead", "reviewer": "Narrative Netrunner Cell", "reviewed_at": "2025-11-06T11:50:00+00:00", "status": "approved"}], "next_actions": []}, "summary": {"problem": "Легендарный профиль Spider Murphy находился в Markdown и требовал стандартизации.", "goal": "Закрепить биографию участницы взрыва Arasaka Tower и защитницы старой NET.", "essence": "Spider Murphy — легенда, скрывающаяся в NET и предупреждающая об угрозах за Blackwall.", "key_points": ["Работала с Рэйчем Бартмоссом и участвовала в разрушении Arasaka Tower.", "Помогла создать Blackwall и паучьи защитные протоколы.", "Оставляет следы в NET, можно встретить её как живую или цифровую сущность."]}, "content": {"sections": [{"id": "history", "title": "История", "body": "Spider Murphy — одна из величайших netrunner всех времён, работавшая с Бартмоссом, участвовавшая во взрыве Arasaka Tower в 2023\\nи создании Blackwall. После катастрофы исчезла, но слухи утверждают, что она по-прежнему защищает NET.\\n", "mechanics_links": [], "assets": []}, {"id": "personality", "title": "Личность", "body": "Загадочная и параноидальная, доверяет лишь считаным друзьям. Живёт ради защиты свободы NET и предотвращения катастроф.\\n", "mechanics_links": [], "assets": []}, {"id": "quests", "title": "Квесты", "body": "«Призрак в сети» — эпическая миссия, где игрок ищет следы Spider Murphy, сталкиваясь с угрозами за Blackwall и возможной встречей с\\nеё наследием или цифровой копией.\\n", "mechanics_links": [], "assets": []}, {"id": "dialogue", "title": "Диалоги", "body": "\\"NET раньше была свободной. Теперь это тюрьма. И мы сами построили стены.\\" — реплика, отражающая её отношение к современным сетям.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "История NET 2020-2093", "link": "../../../../scenarios/tech/net-history-2020-2093.md"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-06", "author": "npc_team", "changes": "Создан легендарный профиль Spider Murphy."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

