INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-sara-miller', 'Сара Миллер — офицер NCPD по набору', '{"metadata": {"id": "npc-sara-miller", "title": "Сара Миллер — офицер NCPD по набору", "document_type": "canon", "category": "narrative", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-12T01:54:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-12T01:54:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["npc", "ncpd", "downtown"], "topics": ["npc-lore", "law-enforcement"], "related_systems": ["narrative-service", "security-service", "social-service"], "related_documents": [{"id": "canon-narrative/factions/security/ncpd-structure.yaml", "relation": "references"}, {"id": "canon-narrative/npc-lore/important/regina-jones.yaml", "relation": "collaborates"}, {"id": "canon-systems-law-enforcement.yaml", "relation": "integrates"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/sara-miller.md", "visibility": "internal", "audience": ["narrative", "systems", "live_ops"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown-описание Сары не охватывало механики NCPD, набор и отношения с игроком.", "goal": "Структурировать офицера NCPD в YAML 1.1.0, выделив набор рекрутов, квесты порядка и систему доверия полиции.", "essence": "Профессиональный офицер Downtown, верящая в порядок и способная вовлечь игрока в службы NCPD.", "key_points": ["Определены цепочки наборов и проверки лояльности к полиции.", "Добавлены социальные связи с полицейскими инициативами и фиксёрами.", "Описаны сценарии последствий выбора стороны игроком."]}, "content": {"sections": [{"id": "identity", "title": "Позиционирование", "body": "Сара Миллер — офицер NCPD, ответственная за набор рекрутов и взаимодействие с наёмниками Downtown. ID `npc-sara-miller`. Поддерживает программу «Serve & Shield» и координирует работу с гражданскими контрактами.\\n", "mechanics_links": [], "assets": []}, {"id": "operations", "title": "Зона ответственности", "body": "Курирует тренировки, выдачу оборудования и распределение заданий. Проводит брифинги в NCPD Station, работает с фиксёрами для найма фрилансеров. Ведёт базу данных рекрутов и программу реабилитации бывших преступников.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и страхи", "body": "Мотивация — поддерживать порядок и укреплять репутацию полиции. Цели: набор надёжных исполнителей, повышение эффективности операций, защита граждан. Страхи: коррупция, срыв миссий, потеря доверия жителей.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и дипломатия", "body": "Союзники: офицеры NCPD, Regina Jones, корпоративные службы безопасности. Оппоненты: банды Watson, коррумпированные чиновники. Игроки влияют на отношения, выбирая сторону закона или преступности.\\n", "mechanics_links": ["canon/narrative/factions/security/ncpd-structure.yaml"], "assets": []}, {"id": "gameplay_hooks", "title": "Системные крючки", "body": "Narrative-service: миссии «Serve & Shield», «Internal Affairs», «Civil Protection». Security-service: патрули, зачистки, аресты. Social-service: программы доверия граждан, медиа-кампании, обучение рекрутов.\\n", "mechanics_links": [], "assets": []}, {"id": "dialogue", "title": "Диалоговый профиль", "body": "Речь формальная, уверенная: «NCPD защищает порядок и граждан». Настраивает игрока на дисциплину, реагирует на выборы. При доверии делится информацией, при нарушениях — отстраняет от заданий.\\n", "mechanics_links": [], "assets": []}, {"id": "arcs", "title": "Арки и исходы", "body": "Арка «Law or Chaos»: игрок решает, поддерживать ли NCPD или помогать преступным фракциям. Исходы: повышение статуса в полиции, компромисс через реформы или разрыв отношений и охота на игрока. Квесты — `ncpd-recruitment`, `order-restoration`, `double-agent`.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "NCPD Operations Manual", "link": "canon/narrative/factions/security/ncpd-structure.yaml"}, {"title": "Law Enforcement Systems Overview", "link": "canon/systems/law-enforcement.yaml"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-12", "author": "narrative_team", "changes": "Конверсия профиля Сары Миллер в YAML, определены механики NCPD и арка выбора игрока."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

