INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-songbird', 'Сонг Со Ми «Songbird» — элитный нетраннер NUSA', '{"metadata": {"id": "npc-songbird", "title": "Сонг Со Ми «Songbird» — элитный нетраннер NUSA", "document_type": "canon", "category": "narrative", "status": "draft", "version": "0.2.0", "last_updated": "2025-11-12T01:40:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["npc", "netrunner", "nusa"], "topics": ["npc-lore", "net-operations"], "related_systems": ["narrative-service", "social-service", "gameplay-service"], "related_documents": [{"id": "mq-phantom-liberty", "relation": "drives"}, {"id": "npc-rosalind-myers", "relation": "reports_to"}, {"id": "canon-lore-net-architecture", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/song-so-mi-songbird.md", "visibility": "internal", "audience": ["narrative", "systems", "live_ops"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль Songbird находился в Markdown и не описывал системные связи с NUSA, нетраннингом и квестовой веткой Phantom Liberty.", "goal": "Сформировать структурированный YAML-документ для использования в сюжетных миссиях, Live Ops и балансировке нетраннерских событий.", "essence": "«Гениальная, но истощённая нетраннерка NUSA, балансирующая между долгом и желанием обрести свободу.»", "key_points": ["Зафиксированы мотивация, страхи и арки, влияющие на решения игрока.", "Добавлены связи с фракциями и системами, выдающие нетраннинг-квесты и последствия.", "Определены диалоговые паттерны и концовки для разветвлённой сюжетной линии."]}, "content": {"sections": [{"id": "identity", "title": "Идентичность и текущий статус", "body": "- Полное имя: Song So Mi, позывной «Songbird», ID `npc-songbird`.\\n- Роль: элитный нетраннер военной программы NUSA, ключевой актив Dogtown.\\n- Локации: защищённые зоны Dogtown, мобильные серверы NUSA, киберпространство.\\n- Состояние: критическое — перегрузка имплантов ставит жизнь под угрозу.\\n", "mechanics_links": [], "assets": []}, {"id": "appearance", "title": "Внешность и импланты", "body": "- Хрупкое телосложение, бледная кожа, следы нейрохирургических операций.\\n- Порты нейроинтерфейсов по позвоночнику, биомониторы на коже, световые индикаторы активности.\\n- Кибервар включает экспериментальные нейролинки военного класса, системы погружения в Net и защиты от ICE.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация, цели и страхи", "body": "- Мотивация: выживание и освобождение от контроля государства.\\n- Цели: найти лечение имплантных повреждений, получить свободу выбора, искупить разрушительные операции.\\n- Страхи: смерть от отказа имплантов, предательство NUSA, потеря личности в Net.\\n- Желания: нормальная жизнь без постоянных миссий, возможность доверять тем, кто рядом.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и фракционное влияние", "body": "- Фракции: NUSA (служит по долгу и боится предательства), Arasaka (враждебна), NetWatch (взаимное уважение, но осторожность), Militech (стратегический нейтралитет).\\n- NPC: Rosalind Myers (куратор), Solomon Reed (оперативный партнер), противники из корпораций и подполья.\\n- Игрок: отношение зависит от поддержки её свободы или интересов NUSA; доверие растёт при помощи в критических миссиях.\\n", "mechanics_links": ["canon/narrative/scenarios/factions/netfactions/netwatch-voodoo-boys-2020-2093.yaml"], "assets": []}, {"id": "gameplay_hooks", "title": "Игровые функции и навыки", "body": "- Narrative-service: ключевой NPC сюжетной линии Phantom Liberty и разветвлённых финалов.\\n- Social-service: модифицирует репутацию NUSA и доступ к секретным операциям.\\n- Gameplay-service: выдаёт высокоуровневые нетраннинг-квесты, quickhacks и доступ к уникальным сетевым миссиям.\\n- Навыки: максимальные хакерские способности, высокие технические параметры, низкие боевые показатели.\\n", "mechanics_links": [], "assets": []}, {"id": "dialogue", "title": "Диалоговые паттерны", "body": "- Приветствия варьируются от настороженного «Ты знаешь, во что ввязался?» до доверительного «С тобой я чувствую шанс».\\n- Ключевые цитаты: «Иногда песня — это код, который спасает», «Свобода — роскошь, которую я не могу себе позволить».\\n- Спецреплики для миссий: «Держись плана, иначе мы оба трупы», «Мы сделали это. Спасибо, что был рядом».\\n", "mechanics_links": [], "assets": []}, {"id": "arcs", "title": "Арка развития и концовки", "body": "- Начало: пленница собственных имплантов и приказов NUSA.\\n- Развитие: сотрудничество с игроком, внутренняя борьба за свободу.\\n- Концовки: побег и новая жизнь, жертва ради миссии, возвращение в NUSA, гибель от перегрузки.\\n- Связанные миссии: «Птица в клетке», «Последняя песня», «Эхо в Net».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Era Index — Phantom Liberty", "link": "canon/narrative/npc-lore/era-index.md"}, {"title": "2077 Game Events", "link": "canon/lore/_03-lore/timeline/2077-game-events.yaml"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.2.0", "date": "2025-11-12", "author": "narrative_team", "changes": "Конверсия профиля Songbird в YAML и расширение игровых связей с NUSA и Phantom Liberty."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

