INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-common-enemy-assault-walker-1822', 'Штурмовой шагоход (уязвимые суставы)', '{"metadata": {"id": "npc-common-enemy-assault-walker-1822", "title": "Штурмовой шагоход (уязвимые суставы)", "document_type": "canon", "category": "narrative", "status": "draft", "version": "1.0.0", "last_updated": "2025-12-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "enemy", "walker", "miniboss"], "topics": ["heavy", "weakpoints"], "related_systems": ["narrative-service", "gameplay-service", "ai-service", "analytics-service"], "related_documents": [{"id": "mechanics-combat-ai-enemies", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/enemies/assault-walker.md", "visibility": "internal", "audience": ["concept", "narrative", "gameplay"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужен профиль тяжёлого шагохода с фронтальным щитом, уязвимыми суставами и фазой перегрева.", "goal": "Зафиксировать параметры щита, weakpoints, перегрева/стана, атаки и телеметрию.", "essence": "Тяжёлый шагоход: фронтальный щит, weakpoints на суставах/спине, перегрев→стан→окно ядра; синергия с инженером.", "key_points": ["Щит фронтально, слабые суставы/спина.", "Перегрев от sustained fire/EMP → стан 3–5с.", "Атаки: пулемётный конус, ракетный залп, топот-шок.", "Синергия: инженер чинит; рой получает укрытие."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Тяжёлый штурмовой шагоход, силён фронтально благодаря щиту, но уязвим на суставах и спине.\\nПерегревается при длительном огне или EMP, что открывает окно стана и урона по ядру.\\n", "mechanics_links": [], "assets": []}, {"id": "abilities", "title": "Способности", "body": "• Фронтальный щит: высокий DR спереди; weakpoints на суставах/спине.  \\n• Перегрев → стан: при sustained fire/EMP; стан 3–5с, окно для урона по ядру.  \\n• Атаки: пулемётный конус, ракетный залп по дистанции, топот-шок вблизи.\\n", "mechanics_links": [], "assets": []}, {"id": "behavior", "title": "Поведение", "body": "• Держит дистанцию, разворачивает щит к главной угрозе.  \\n• При низком HP зовёт дронов/турели, если инженер рядом — чинится.  \\n• Меняет тип атаки по дистанции.\\n", "mechanics_links": [], "assets": []}, {"id": "counters", "title": "Контр-игра", "body": "• Фланг по суставах/спине, фокус в перегрев.  \\n• EMP для ускорения перегрева.  \\n• Укрытия от ракет, разрыв дистанции от топота.\\n", "mechanics_links": [], "assets": []}, {"id": "telemetry", "title": "Телеметрия", "body": "• time_to_kill, weakpoint_damage_share, overheat_events, stun_windows_used.  \\n• shield_blocked_damage, drone_calls, emp_triggers.  \\n• Цели: TTK сбалансирован, weakpoint_share высок, EMP как надёжный контр.\\n", "mechanics_links": [], "assets": []}, {"id": "loot", "title": "Лут", "body": "• Бронепластины, ракетные детали, ядро шагохода; редкий blueprint на перегрев-мод.  \\n• Низкие креды.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1822, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-12-12", "author": "content_writer", "changes": "Создан профиль штурмового шагохода с weakpoints, перегревом и телеметрией."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

