INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-solo-rogue-2000-2077', 'Rogue Amendiares — Legendary Solo & Fixer (2000–2077)', '{"metadata": {"id": "npc-solo-rogue-2000-2077", "title": "Rogue Amendiares — Legendary Solo & Fixer (2000–2077)", "document_type": "canon", "category": "narrative", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-06T12:08:00+00:00", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "afterlife", "legend"], "topics": ["night-city-history", "legendary-solos"], "related_systems": ["narrative-service", "quest-service"], "related_documents": [{"id": "scenarios-night-city-afterlife-bar-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/solo/rogue-amendiares-legend-2000-2077.md", "visibility": "internal", "audience": ["concept", "narrative", "quest-design"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужно закрепить легендарную фигуру Rogue Amendiares и её влияние на лор Night City.", "goal": "Сконцентрировать ключевые факты, отношения и цитаты для использования в квестах и диалогах.", "essence": "Роуг — легендарный фиксер Afterlife, бывший Solo, управлявшая подпольем Night City до гибели в 2077 году.", "key_points": ["Активна в 2000–2077 гг., умерла при штурме Arasaka Tower.", "Владелица Afterlife и ключевой фиксер города.", "Близкие связи с Джонни Сильверхэндом и участие в корпвойнах."]}, "content": {"sections": [{"id": "identity", "title": "Основные сведения", "body": "• Жизненный период: 1960–2077, легендарный Solo и фиксер Night City.\\n• Локация: бар Afterlife, где управляла контрактами наёмников.\\n• Прозвища: Rogue, «Королева Night City».\\n• Статус: икона андеграунда, определяющая стандарты найма фиксеров.\\n", "mechanics_links": [], "assets": []}, {"id": "history", "title": "История и путь", "body": "• До 2023: работала с Morgan Blackhand и Джонни Сильверхэндом, участвовала в операциях против корпораций.\\n• После Четвёртой корпоративной войны: ушла с поля, выкупила Afterlife, стала главной фигуры подполья.\\n• 2077: вернулась к боевым действиям, помогая V в штурме Arasaka Tower; погибла в бою, оставаясь легендой.\\n", "mechanics_links": [], "assets": []}, {"id": "personality", "title": "Характер", "body": "• Жёсткая, циничная, мудрая. Видела взлёты и падения легенд, сохраняет холодный расчёт.\\n• Ценит профессионализм и готовность платить цену за легендарный статус.\\n• Сложные отношения с Джонни Сильверхэндом: любовь, предательство, прощение.\\n", "mechanics_links": [], "assets": []}, {"id": "quests", "title": "Известные события и квесты", "body": "• Финальный штурм Arasaka Tower — помогает V и жертвует собой ради победы над Адамом Смашером.\\n• Выдаёт самые опасные и дорогие контракты Night City, определяя, кто достоин статуса легенды.\\n• Сохраняет контроль над Afterlife, определяя иерархию наёмников.\\n", "mechanics_links": [], "assets": []}, {"id": "quotes", "title": "Цитаты", "body": "> \\"Я правила этим городом 50 лет. Видела, как легенды рождаются и умирают. Ты хочешь стать легендой? Будь готов умереть за это.\\"\\n> \\"Джонни... ты всегда был эгоистичным ублюдком. Но я любила тебя. Проклятье.\\"\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": ["Cyberpunk 2077", "Cyberpunk 2020"], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_director", "changes": "Конвертация легендарного профиля Rogue Amendiares из MD в YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

