INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-viktor-vektor', 'Viktor Vektor — профиль NPC', '{"metadata": {"id": "npc-viktor-vektor", "title": "Viktor Vektor — профиль NPC", "document_type": "canon", "category": "narrative", "status": "draft", "version": "0.2.0", "last_updated": "2025-11-12T00:25:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["npc", "ripperdoc", "support"], "topics": ["npc-lore", "medical-systems"], "related_systems": ["narrative-service", "health-service", "social-service"], "related_documents": [{"id": "canon-lore-misty-olshevski", "relation": "ally"}, {"id": "mq-medical-arc", "relation": "drives"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/viktor-vektor.md", "visibility": "internal", "audience": ["narrative", "gameplay", "live_ops"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль Виктора Вектора не был структурирован в YAML и не связывался с медицинскими сервисами игры.", "goal": "Задокументировать риппердока как ключевой узел имплантов, лечения и анти-киберпсихозных сценариев.", "essence": "«Заботливый риппердок, удерживающий игроков от киберпсихоза и дающий доступ к имплантам высшего класса».", "key_points": ["Виктор — главный наставник по имплантам для раннего и mid-game контента.", "Балансирует медицину и человечность, влияя на метрики киберпсихоза.", "Поддерживает нейтральные отношения со всеми фракциями ради помощи пациентам."]}, "content": {"sections": [{"id": "identity", "title": "Идентичность", "body": "- Имя: Viktor Vektor, ID `npc-viktor-vektor`, риппердок Downtown.\\n- Происхождение: Street Kid, бывший профессиональный боксёр, перешёл в медицину.\\n- Оперирует собственной клиникой, обслуживая игроков и NPC вне корпоративной зависимости.\\n", "mechanics_links": [], "assets": []}, {"id": "appearance", "title": "Внешность и стиль", "body": "- Мужчина 50–55 лет, высокий и крепкий, с шрамами боксёра.\\n- Предпочитает практичный медицинский гардероб: халаты, перчатки, функциональная обувь.\\n- Импланты среднего уровня: связь, медицинские инструменты, аналитические модули.\\n", "mechanics_links": [], "assets": []}, {"id": "history", "title": "История и мотивация", "body": "- Вырос на улицах Night City, прошёл путь от боксёра до хирурга-имплантолога.\\n- Открыл клинику, чтобы помогать сообществу и удерживать людей от киберпсихоза.\\n- Мотивация: поддерживать здоровье игроков, расставлять акценты на человеческой стороне имплантов.\\n- Боится роста киберпсихоза и утраты способности помогать.\\n", "mechanics_links": [], "assets": []}, {"id": "personality", "title": "Личность и манеры", "body": "- Заботливый и мудрый наставник с дружелюбной манерой общения.\\n- Использует неформальные обращения («чомбата»), напоминает о балансе имплантов и человечности.\\n- Дружелюбный тон и готовность помочь формируют доверие игроков.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и взаимодействия", "body": "- Фракции: сохраняет нейтралитет, работает со всеми в нужде.\\n- NPC: близок с Misty и другими медиками; избегает прямых конфликтов.\\n- Игрок: стартует с дружелюбного отношения; рост доверия идёт через повторные услуги и поддержку антикиберпсихозных программ.\\n", "mechanics_links": ["canon/narrative/npc-lore/important-npcs-list.yaml"], "assets": []}, {"id": "gameplay", "title": "Игровое значение", "body": "- Основные функции: установка имплантов, лечение, консультации по киберпсихозу, выдача медицинских квестов.\\n- Квесты: «Первые импланты», «Киберпсихоз», «Медицинская помощь», «Баланс».\\n- Навыки: высокие технические и социальные показатели, средние боевые и низкие хакерские.\\n", "mechanics_links": ["mechanics/health/cyberpsychosis.yaml"], "assets": []}, {"id": "dialogue", "title": "Диалоговые блоки", "body": "- Приветствия: от «Привет, чомбата» до строгого «Что тебе нужно? Быстро» при низкой репутации.\\n- Основные темы: профилактика киберпсихоза, баланс имплантов и человечности, поддержка пациентов.\\n- Спецреплики при выполнении и завершении медицинских заданий усиливают наставническую роль.\\n", "mechanics_links": [], "assets": []}, {"id": "progression", "title": "Развитие персонажа", "body": "- Начало: риппердок, открывающий игроку доступ к базовым имплантам.\\n- Развитие: сопровождает игрока во внедрении сложных модификаций и борьбе с киберпсихозом.\\n- Финал: остаётся опорой для игроков, формируя устойчивые медицинские сервисы.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Базовые диалоги NPC", "link": "canon/narrative/dialogues/npc-dialogues-basic.yaml"}, {"title": "План основных квестов", "link": "canon/narrative/quests/main/main-quests-outline.yaml"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.2.0", "date": "2025-11-12", "author": "narrative_team", "changes": "Конверсия профиля Viktor Vektor в YAML и описание медицинских связей."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

