INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-wakako-okada', 'Вакако Окада — матриарх контрактов Westbrook', '{"metadata": {"id": "npc-wakako-okada", "title": "Вакако Окада — матриарх контрактов Westbrook", "document_type": "canon", "category": "narrative", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-12T01:34:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-12T01:34:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["npc", "fixer", "westbrook"], "topics": ["npc-lore", "contract-design"], "related_systems": ["narrative-service", "social-service", "economy-service"], "related_documents": [{"id": "canon-narrative/factions/gangs/tyger-claws-profile", "relation": "collaborates"}, {"id": "canon-narrative/npc-lore/important/hiromi-sato.yaml", "relation": "shares_territory"}, {"id": "canon-lore/nightlife/japantown-network.yaml", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important/wakako-okada.md", "visibility": "internal", "audience": ["narrative", "systems", "live_ops"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание Вакако находилось в Markdown и не фиксировало системные крючки контрактов Westbrook.", "goal": "Структурировать профиль фикcера для сценариев Japantown, дипломатии Tyger Claws и цепочек контрактов с высоким риском.", "essence": "Элегантный куратор сделок, балансирующий традиции, корпоративные интересы и криминальный порядок в Westbrook.", "key_points": ["Зафиксирована сеть влияния Вакако и параметры контрактов, влияющих на репутацию игрока.", "Добавлены связи с Tyger Claws, корпорациями и независимыми наёмниками.", "Определены геймплейные крючки для stealth, дипломатии и премиальных наград economy-service."]}, "content": {"sections": [{"id": "identity", "title": "Позиционирование и влияние", "body": "Вакако Окада — ключевой фиксер Westbrook и Japantown. ID `npc-wakako-okada`. Управляет сетью салонов, клубов и информационных брокеров. Поддерживает строгий протокол встреч и персонализированные контрактные пакеты для исполнителей.\\n", "mechanics_links": [], "assets": []}, {"id": "operations", "title": "Операции и инфраструктура", "body": "Контролирует цепочку «Okada Contracts» — VIP-офисы, капсулы переговоров, канал связи с Tyger Claws. Использует традиционные кодексы и цифровые подписи. Заказчики: корпорации, криминальные синдикаты, элита Night City. Ведёт архив выполненных задач и рейтингов исполнителей.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и стратегия", "body": "Мотивация — поддерживать порядок в Westbrook и укреплять личный авторитет. Цели: удерживать баланс сил, расширять сеть информаторов, выводить элитные контракты на рынок. Страхи: нарушение дисциплины, непредсказуемые исполнители, падение репутации.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и партнёры", "body": "Фракции: Tyger Claws (стратегический союз), корпорации Arasaka и Kang Tao (деловые переговоры), независимые фиксёры (прагматичный обмен ресурсами). Игроки получают доступ к контрактам после проверки репутации и соблюдения кодекса. Нарушение условий ведёт к блокировке доступа.\\n", "mechanics_links": ["canon/narrative/scenarios/factions/gangs/tyger-claws.yaml"], "assets": []}, {"id": "gameplay_hooks", "title": "Системные крючки", "body": "Narrative-service: цепочки «Император Westbrook», «Тихие сделки», «Золотой список». Social-service: переговоры, дипломатические проверки, контрактные переговоры с многоступенчатым доверием. Economy-service: премиальные награды, скидки у поставщиков, эксклюзивные модификации.\\n", "mechanics_links": [], "assets": []}, {"id": "dialogue", "title": "Диалоговый профиль", "body": "Манера речи — сдержанная и хладнокровная: «Контракты не терпят суеты. Принеси результат — получишь больше». Использует уважительные обращения, но быстро пресекает непрофессионализм. Спецреплики активируются при обоюдной выгоде или провале сроков.\\n", "mechanics_links": [], "assets": []}, {"id": "arcs", "title": "Арки и сценарии", "body": "Арка «Сердце Japantown»: игрок помогает удерживать баланс между корпорациями и Tyger Claws. Финалы: усиление влияния Вакако, передача части сети наследникам или потеря контроля при предательстве. Связанные квесты — `westbrook-silent-strike`, `tyger-claws-diplomacy`, `vip-dispute`.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Nightlife Japantown Overview", "link": "canon/lore/nightlife/japantown-network.yaml"}, {"title": "Tyger Claws Operations", "link": "canon/narrative/factions/gangs/tyger-claws-profile.yaml"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-12", "author": "narrative_team", "changes": "Конверсия Markdown-профиля Вакако Окады в YAML с добавлением системных крючков и арок контрактов."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

