INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('canon-narrative-npc-important-list', 'Список важных NPC NECPGAME', '{"metadata": {"id": "canon-narrative-npc-important-list", "title": "Список важных NPC NECPGAME", "document_type": "canon", "category": "narrative", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_director", "contact": "narrative@necp.game"}], "tags": ["npc", "catalog"], "topics": ["narrative", "characters"], "related_systems": ["narrative-service"], "related_documents": [{"id": "canon-narrative-npc-era-index", "relation": "complements"}, {"id": "canon-narrative-npc-template", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/important-npcs-list.md", "visibility": "internal", "audience": ["narrative", "quest-design"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Каталог важных NPC существовал в Markdown и содержал несогласованный перечень с отметками о доработках.", "goal": "Структурировать список ключевых персонажей, разделить их по категориям и задать ориентиры для дальнейшей проработки.", "essence": "Документ описывает важных NPC канонного лора и авторских персонажей, выделяет их функции и связи с фракциями.", "key_points": ["Персонажи сгруппированы по происхождению (канон Cyberpunk и авторские герои).", "Категории фиксируют роль фиксеров, лидеров, торговцев, риппердоков и нетраннеров.", "Указан backlog расширений для будущих итераций."]}, "content": {"sections": [{"id": "overview", "title": "Общее описание", "body": "Каталог охватывает ключевых персонажей NECPGAME, комбинируя наследие Cyberpunk и авторские разработки. Документ служит\\nнавигацией для сценарных и квестовых команд.\\n", "mechanics_links": [], "assets": []}, {"id": "canon_characters", "title": "Персонажи лора Cyberpunk", "body": "Описаны корпоративные, бандитские и независимые NPC, адаптированные для MMO: Hiroshi Tanaka, James “Iron” Reed,\\nJose “Tiger” Ramirez, Royce, Marco “Fix” Sanchez и Viktor Vektor.\\n", "mechanics_links": [], "assets": []}, {"id": "authored_characters", "title": "Авторские NPC", "body": "Приведены оригинальные герои, такие как Sara Miller, Jake Archer, Rita Moreno и Anna Petrova, чьи роли поддерживают\\nMVP-цепочки и социальные взаимодействия.\\n", "mechanics_links": [], "assets": []}, {"id": "categories", "title": "Категории и функции", "body": "Персонажи классифицированы на фиксеров, лидеров фракций, торговцев, риппердоков, нетраннеров, edgerunners и представителей\\nPhantom Liberty. Для каждой группы указаны примеры и ответственность.\\n", "mechanics_links": [], "assets": []}, {"id": "backlog", "title": "Бэклог расширений", "body": "Сформирован перечень NPC для последующей детализации: дополнительные фиксеров, корпоратов, лидеров банд, сотрудников\\nNCPD, нетраннеров, риппердоков и представителей ночной жизни и Edgerunners.\\n", "mechanics_links": [], "assets": []}, {"id": "priorities", "title": "Приоритеты развития каталога", "body": "Требуется дополнить карточки подробными историями, мотивацией и ветвями взаимодействий, а также расширить спектр\\nперсонажей за счёт новых канонных и авторских героев.\\n", "mechanics_links": [], "assets": []}, {"id": "references", "title": "Связанные материалы", "body": "Указаны каталоги детальных описаний, историй и отношений NPC, обеспечивающие углублённое изучение лора.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Каталог важных NPC", "link": "./important/"}, {"title": "Истории NPC", "link": "./important/npc-stories.md"}, {"title": "Отношения NPC", "link": "./important/npc-relationships.md"}], "decisions": []}, "implementation": {"needs_task": false, "github_issue": 108, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "narrative_team", "changes": "Список важных NPC перенесён в YAML, структурированы категории и бэклог."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

