INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('canon-narrative-npc-misty-olszewski', '[NPC] Мисти Олшевски — хранительница человечности', '{"metadata": {"id": "canon-narrative-npc-misty-olszewski", "title": "[NPC] Мисти Олшевски — хранительница человечности", "document_type": "canon", "category": "narrative", "status": "approved", "version": "1.0.0", "last_updated": "2025-12-13T19:20:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-22T14:42:04+00:00", "owners": [{"role": "content_writer", "contact": "content@necp.game"}], "tags": ["npc", "locations", "mystic", "healer", "spiritual"], "topics": ["narrative", "gameplay", "mysticism"], "related_systems": ["narrative-service", "quest-service", "dialogue-service"], "related_documents": [{"id": "canon-narrative-npc-locations-list", "relation": "references"}, {"id": "canon-narrative-dialogue-mystic-lines", "relation": "complements"}], "source": "knowledge/canon/narrative/npc-lore/locations/misty-olszewski.yaml", "visibility": "internal", "audience": ["content", "narrative-design", "quest-design"], "risk_level": "low"}, "review": {"chain": [{"role": "content_writer", "reviewer": "Content Writer Agent", "reviewed_at": "2025-12-13T19:20:00+00:00", "status": "approved"}], "next_actions": []}, "summary": {"problem": "Необходим духовный NPC для баланса между технологией и человечностью", "goal": "Создать мистическую целительницу с глубоким пониманием человеческой природы", "essence": "Таинственная женщина с паранормальными способностями и мудростью древних традиций", "key_points": ["Паранормальные способности к предвидению и исцелению", "Хранительница баланса между технологией и душой", "Связи с древними традициями и уличной магией", "Ментор в вопросах человечности и морали"]}, "content": {"identity": {"name": "Misty Olszewski", "aliases": ["Misty", "The Mystic", "Soul Keeper", "Oracle of the Streets"], "unique_id": "npc-misty-olszewski-001", "category": "locations", "type": "mystic_healer", "era": "2050-2093", "active_years": "2050-2093", "threat_level": "low", "faction_affiliation": "independent"}, "context": {"factions": ["independent", "ancient_traditions", "street_mystics", "nomad_spiritualists"], "locations": ["misty_mystic_shop", "nomad_camps", "ancient_ruins", "meditation_sites"], "related_services": ["narrative-service", "quest-service", "dialogue-service"], "api_endpoints": ["/api/v1/npc/misty-olszewski"], "frontend_modules": ["DialogueSystem", "QuestNPC"]}, "appearance_style": {"visual_description": "Загадочная женщина средних лет с длинными темными волосами.\\nНосит эклектичную одежду: элементы традиционной одежды, кристаллы, татуировки.\\nГлаза кажутся видящими сквозь время и пространство.\\nДвижения грациозные, но уверенные, как у хищника в засаде.\\n", "implants": ["natural_resonance", "empathy_amplifier", "memory_palace"], "clothing_style": "mystic_traditional_fusion", "speech_patterns": "- \\"The threads of fate weave strange patterns...\\"\\n- \\"Your aura speaks volumes. What troubles your soul?\\"\\n- \\"Technology may enhance, but it cannot replace the human spirit.\\"\\n- \\"Listen to the whispers of your ancestors.\\"\\n"}, "history": {"origin": "Родилась в семье польских иммигрантов в Night City.\\nОбнаружила паранормальные способности в подростковом возрасте.\\nСтала хранительницей древних традиций в мире высоких технологий.\\n", "key_events": ["2050: Открытие паранормальных способностей", "2060: Основание мистического салона в Little Warsaw", "2070: Стала духовным наставником для уличных жителей", "2080: Участие в сопротивлении total digitization"], "current_status": "spiritual_guide", "motivations": "- Сохранение человеческой души в цифровом мире\\n- Помощь потерянным душам найти путь\\n- Защита древней мудрости от забвения\\n- Поиск гармонии между традициями и прогрессом\\n"}, "relationships": {"faction_relations": [{"faction": "nomads", "initial_relation": "respectful", "development_factors": ["spiritual_connection", "traditional_values"]}, {"faction": "corporations", "initial_relation": "wary", "development_factors": ["technology_balance", "human_cost_awareness"]}], "npc_relations": [{"type": "nomad_elder", "relation": "student_teacher", "synergy": "wisdom_sharing"}, {"type": "street_kids", "relation": "protective_motherly", "synergy": "guidance_providing"}, {"type": "cyberpsychos", "relation": "healing_redemptive", "synergy": "soul_restoration"}], "player_interactions": [{"initial_relation": "mysterious_welcoming"}, {"development_factors": ["spiritual_awareness", "moral_choices", "empathy_shown"]}]}, "gameplay_role": {"primary_function": "spiritual_guide", "secondary_functions": ["healer", "oracle"], "related_quests": ["soul_searching", "traditional_rituals", "cyberpsycho_curing"], "related_events": ["spiritual_awakenings", "traditional_ceremonies"], "rewards": ["spiritual_insights", "traditional_knowledge", "healing_abilities"], "consequences": ["spiritual_awareness_changes", "moral_alignment_shifts", "hidden_knowledge_access"]}, "mechanics": {"dialogue_options": [{"spiritual_guidance": "life_advice"}, {"divination_reading": "future_insights"}, {"healing_services": "soul_restoration"}, {"traditional_teaching": "wisdom_sharing"}], "quest_triggers": [{"spiritual_crisis": "emotional_turmoil"}, {"traditional_seeking": "cultural_connection"}, {"cyberpsycho_threat": "mental_instability"}], "mysticism_system": [{"spiritual_level": "1-10"}, {"insights_unlocked": "empathy, intuition, balance"}, {"aura_reading": "personality_analysis"}]}, "dialogues": {"greeting_lines": ["Welcome, seeker. What whispers in your soul today?", "The cards have been waiting for you. Shall we see what they reveal?", "Your aura is troubled. Come, let me help you find clarity."], "quest_lines": ["The spirits guide you to a path of discovery. Will you walk it?", "There''s darkness in your future. But light can be found.", "The old ways call to you. Are you ready to listen?"], "farewell_lines": ["May the spirits guide your path.", "Remember: the soul''s journey never ends.", "Come back when you need to find yourself again."]}, "inspiration": {"cyberpunk_sources": ["Cyberpunk 2077: Misty character and mysticism", "Fusion of Eastern European folklore and cyberpunk", "Balance between technology and spirituality"], "unique_elements": ["Paranormal abilities in technological world", "Preservation of human essence", "Mystical guidance in moral dilemmas"]}, "maintenance": {"open_questions": [], "visual_needs": ["mystic_shop_interior", "tarot_card_animations", "aura_effect_particles"], "quest_integration": ["spiritual_questline", "character_development", "moral_choice_system"], "change_history": [{"version": "1.0.0", "date": "2025-12-13", "author": "content_writer_agent", "changes": "Initial implementation of Misty Olszewski NPC"}]}}, "appendix": {"glossary": [{"term": "Aura Reading", "definition": "Mystical perception of person''s spiritual state"}, {"term": "Soul Keeper", "definition": "Guardian of human essence in technological age"}], "references": [{"title": "Mysticism in Cyberpunk Lore", "link": "../../lore/themes/spirituality-technology.yaml"}, {"title": "Spiritual Quest Templates", "link": "../../../content/quest-templates/soul-journey.yaml"}], "decisions": [{"rationale": "Spiritual guidance for players", "impact": "Adds depth to character development"}, {"rationale": "Balance between tech and humanity", "impact": "Provides philosophical framework"}]}, "implementation": {"needs_task": false, "github_issue": 140875182, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-12-13", "author": "content_writer_agent", "changes": "Created Misty Olszewski NPC specification"}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

