INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-common-enemy-boss-networker-1828', 'Босс-нетворкер (сетевой AI)', '{"metadata": {"id": "npc-common-enemy-boss-networker-1828", "title": "Босс-нетворкер (сетевой AI)", "document_type": "canon", "category": "narrative", "status": "draft", "version": "1.0.0", "last_updated": "2025-12-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "enemy", "boss", "netrunner", "raid"], "topics": ["raid", "hacking"], "related_systems": ["narrative-service", "gameplay-service", "ai-service", "analytics-service"], "related_documents": [{"id": "mechanics-combat-ai-enemies", "relation": "references"}, {"id": "mechanics-combat-combat-hacking", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/enemies/boss-networker.md", "visibility": "internal", "audience": ["concept", "narrative", "gameplay"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужен профиль босс-нетраннера с фазами демонов, ICE-волной и уязвимостью ядра.", "goal": "Зафиксировать фазы, урон/длительность глитчей, подкрепления и телеметрию.", "essence": "Двухслойный бой: физика + взлом. Демоны, ICE-волны, окна уязвимости ядра, вызовы подкреплений.", "key_points": ["Фазы: демоны → ICE-волна → уязвимость ядра.", "HUD-глитчи, блок имплантов, мульти-таргет.", "Подкрепления, паттерн меняется при взломе ядра.", "Телеметрия: длительность, участие нетраннеров, вайпы."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Сетевой босс, управляет боем в двух слоях: физическом и хакерском. Спавнит демонов, бьёт по HUD,\\nзапускает ICE-волны и открывает ядро в короткие окна уязвимости. Меняет паттерн после успешного взлома.\\n", "mechanics_links": [], "assets": []}, {"id": "phases", "title": "Фазы боя", "body": "• Фаза 1: спавн демонов, давление на HUD, ограниченный блок имплантов.  \\n• Фаза 2: ICE-волна, снижение эффективности имплантов, AOE глитчи.  \\n• Фаза 3: ядро уязвимо 10–14с, требуется взлом; после — цикл повторяется с усилением.  \\n• Подкрепления вызываются при падении HP порогов (70/40%).\\n", "mechanics_links": [], "assets": []}, {"id": "abilities", "title": "Способности", "body": "• Демоны: волны каждые 14–20с, давление на нетраннеров.  \\n• ICE-волна: пульс каждые 22–30с, дебафф имплантов на 8–12с.  \\n• HUD-глитчи: помехи/блики 1–2с, кд 10–14с.  \\n• Блок имплантов: короткий блок 4–6с в радиусе 12–18 м, кд 24–32с.\\n", "mechanics_links": [], "assets": []}, {"id": "behavior", "title": "Поведение", "body": "• Держит дистанцию, мульти-таргет: физически отступает, цифрово атакует деку.  \\n• Меняет паттерн при взломе ядра, вызывает подкрепления.  \\n• Уязвим к анти-ICE/EMP в окне уязвимости.\\n", "mechanics_links": [], "assets": []}, {"id": "counters", "title": "Контр-игра", "body": "• Координация: разделить роли бойцов и нетраннеров.  \\n• Анти-ICE дек и EMP для окна ядра.  \\n• Сбивать демонов/подкрепления быстро, чтобы открыть время на взлом.\\n", "mechanics_links": [], "assets": []}, {"id": "telemetry", "title": "Телеметрия", "body": "• fight_duration, netrunner_participation_rate, wipes_count.  \\n• core_hack_success_rate, demon_wave_kill_time, ice_wave_hits.  \\n• Цели: fight 120–180с, core_hack_success ~60% при двух нетраннерах, wipes ≤2 на прогресс-группе.\\n", "mechanics_links": [], "assets": []}, {"id": "loot", "title": "Лут", "body": "• Редкие чипы, анти-ICE софт, уникальные косметические трофеи.  \\n• Квестовый ключ ядра, данные для прогресса.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1828, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-12-12", "author": "content_writer", "changes": "Создан профиль босс-нетворкера с фазами демонов, ICE-волной, окном ядра и телеметрией."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

