INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('canon-npc-netrunner-ghostwalker-2065-2090', '"Ghost Walker" — Blackwall Diver', '{"metadata": {"id": "canon-npc-netrunner-ghostwalker-2065-2090", "title": "\\"Ghost Walker\\" — Blackwall Diver", "document_type": "canon", "category": "narrative", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-06T11:54:00+00:00", "owners": [{"role": "npc_lead", "contact": "npc@necp.game"}], "tags": ["npc", "netrunner", "blackwall"], "topics": ["narrative", "netrunning"], "related_systems": ["npc-service"], "related_documents": [{"id": "canon-scenarios-tech-blackwall-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/netrunners/ghost-walker-blackwall-diver-2065-2090.md", "visibility": "internal", "audience": ["narrative", "gameplay"], "risk_level": "high"}, "review": {"chain": [{"role": "npc_lead", "reviewer": "Narrative Netrunner Cell", "reviewed_at": "2025-11-06T11:54:00+00:00", "status": "approved"}], "next_actions": []}, "summary": {"problem": "Профиль Ghost Walker требовал переноса в YAML для интеграции с базой знаний netrunner.", "goal": "Закрепить образ нелегального дайвера Blackwall и его влияние на миссии.", "essence": "Ghost Walker возвращается из-за Blackwall с запрещённой информацией, меняя баланс сил и рискуя заражением ИИ.", "key_points": ["Регулярно погружается за Blackwall и продаёт редкие данные.", "Считается союзником или угрозой для Voodoo Boys и корпораций.", "Предлагает опасные миссии с высокими наградами."]}, "content": {"sections": [{"id": "history", "title": "История", "body": "Ghost Walker — один из немногих, кто погружается за Blackwall и возвращается. Он торгует фрагментами старой NET, технологиями\\nдо-Blackwall эпохи и заражает структуру корпораций уникальной информацией. Каждое погружение оставляет следы в его сознании.\\n", "mechanics_links": [], "assets": []}, {"id": "personality", "title": "Личность", "body": "Отрешённый и странный, часто говорит загадками и слышит \\"голоса\\" за барьером. Его поведение вызывает подозрения в заражении ИИ.\\n", "mechanics_links": [], "assets": []}, {"id": "quests", "title": "Квесты", "body": "«За барьером» — очень опасная миссия, где игрок сопровождает Ghost Walker в погружении за Blackwall ради доступа к легендарным данным.\\n", "mechanics_links": [], "assets": []}, {"id": "dialogue", "title": "Диалоги", "body": "\\"За Blackwall нет времени. Там только вечность. И они ждут. Всегда ждут.\\" — ключевая реплика, иллюстрирующая психологическое\\nсостояние персонажа.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "История Blackwall", "link": "../../../../scenarios/tech/blackwall-2020-2093.md"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-06", "author": "npc_team", "changes": "Создан профиль Ghost Walker."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

