-- NPC: npc-netrunner-tbug-2050-2077
INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-netrunner-tbug-2050-2077', 'T-Bug — натруннер-профи (2050-2077)', '{"metadata": {"id": "npc-netrunner-tbug-2050-2077", "title": "T-Bug — натруннер-профи (2050-2077)", "document_type": "canon", "category": "npc-lore", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "netrunner", "night-city"], "topics": ["netrunning", "corporate-heists"], "related_systems": ["narrative-service", "worldbuilding-service"], "related_documents": [{"id": "scenarios-tech-net-history-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/netrunners/t-bug-prodigy-2050-2077.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "История T-Bug существовала в Markdown и не была синхронизирована с нарративными документами Night City.", "goal": "Зафиксировать роль T-Bug в ограблении Konpeki Plaza и сохранить материалы для линейки миссий о предательстве.", "essence": "Осторожная профессионалка предупреждала о рисках, но погибла от ICE Arasaka, оставив цифровые следы.", "key_points": ["Знаковый нетраннер, сопровождавший команду V и Jackie в Konpeki Plaza в 2077.", "Погибла от чёрного ICE Arasaka, что стало эмоциональным ударом для команды.", "Оставила зашифрованные журналы, которые можно использовать в квестах мести или расследования."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "T-Bug — один из самых востребованных нетраннеров Watson. Работала из скрытой студии и брала только\\nтщательно подготовленные заказы, при этом сохраняла безупречную репутацию «чистой работы».\\n", "mechanics_links": [], "assets": []}, {"id": "history", "title": "История и трагедия", "body": "В 2050-х T-Bug начала карьеру с корпоративных взломов, избегая огласки. В 2077 году согласилась на\\nоперацию против Arasaka и подключалась к команде V и Jackie на расстоянии. Предупреждала о рисках,\\nоднако черный ICE вычислил её подключение и остановил сердце. Последние логи содержат фрагменты\\nоб утечке информации об ограблении.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "personality", "title": "Личность и стиль работы", "body": "Спокойная, методичная, практичная. Избегала лишней агрессии, опираясь на расчёт и прогноз. Предпочитала\\n«невидимые» операции, оставляя минимум следов и поддерживая дистанцию даже с партнёрами.\\n", "mechanics_links": [], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- Расследование предательства, приведшего к гибели T-Bug, с извлечением её зашифрованного послания.\\n- Контракт на использование её старых эксплойтов против конкурирующих корпораций.\\n- Виртуальная реконструкция последних минут T-Bug для обучения новых нетраннеров.\\n", "mechanics_links": [], "assets": []}, {"id": "quotes", "title": "Цитаты", "body": "> «Чистая работа — это когда никто не знает, что ты был там. Даже цель.»\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль T-Bug в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

