INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('canon-npc-prioritization-matrix', 'NPC Readiness — Матрица приоритизации', '{"metadata": {"id": "canon-npc-prioritization-matrix", "title": "NPC Readiness — Матрица приоритизации", "document_type": "canon", "category": "narrative", "status": "in-review", "version": "1.0.0", "last_updated": "2025-11-08T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_producer", "contact": "narrative@necp.game"}], "tags": ["npc", "prioritization"], "topics": ["npc-management", "quest-planning"], "related_systems": ["narrative-service", "quest-handoff-pipeline"], "related_documents": [{"id": "canon-npc-important-list", "relation": "references"}, {"id": "canon-npc-template", "relation": "references"}, {"id": "canon-main-quests-template-plan", "relation": "aligns"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/npc-prioritization.md", "visibility": "internal", "audience": ["narrative", "lore", "production"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}, {"role": "production_manager", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Перечень ключевых NPC существовал в Markdown и не синхронизировался с матрицей приоритетов и дедлайнами подготовки.", "goal": "Сконсолидировать приоритеты, ответственных и статусы NPC в структуре знаний для быстрого планирования сюжетных сценариев.", "essence": "Документ определяет матрицу готовности NPC к основным квестам, требования к шаблонам и чек-лист подготовки.", "key_points": ["Матрица фиксирует приоритеты P0–P2, владельцев и сроки для восьми ключевых NPC.", "Шаблон NPC требует блоков Motivation, Arcs, Quest Hooks, Dialogue Beats и поля readiness.", "После обновления шаблонов данные синхронизируются с трекером статусов."]}, "content": {"sections": [{"id": "objectives", "title": "Цель документа", "body": "Определить приоритеты подготовки NPC и назначить владельцев для заполнения шаблонов `NPC-TEMPLATE`.\\nОбеспечить выравнивание с главным планом квестов и дедлайнами нарративной команды.\\n", "mechanics_links": [], "assets": []}, {"id": "prioritization_matrix", "title": "Матрица NPC", "body": "| NPC | Квесты | Шаблон | Приоритет | Владелец | Дедлайн | Статус |\\n|-----|--------|--------|-----------|----------|---------|--------|\\n| Виктор Вектор | MQ-001, MQ-003 | important/viktor-vektor.md | P0 | MedTech Writer | 2025-11-09 | in_review |\\n| Padre Ibarra | MQ-002, MQ-201 | important/padre-ibarra.md | P0 | Street Writer | 2025-11-10 | draft |\\n| Hanako Arasaka | MQ-002, MQ-101 | important/hanako-arasaka.md | P0 | Corpo Writer | 2025-11-10 | draft |\\n| James \\"Iron\\" Reed | MQ-102 | important/militech-james-reed.md | P1 | Corpo Writer | 2025-11-12 | missing |\\n| Royce | MQ-201 | important/royce.md | P1 | Street Writer | 2025-11-11 | missing |\\n| Marko \\"Fix\\" Sanchez | MQ-001 | important/marko-sanchez.md | P1 | Fixer Writer | 2025-11-11 | draft |\\n| Anders Hellman | MQ-101 | important/anders-hellman.md | P1 | Corpo Writer | 2025-11-12 | draft |\\n| Susie Q | MQ-202 | important/susie-q.md | P2 | Nightlife Writer | 2025-11-14 | ready |\\n", "mechanics_links": [], "assets": []}, {"id": "template_requirements", "title": "Требования к шаблону NPC", "body": "Использовать обязательные блоки Motivation, Arcs, Quest Hooks и Dialogue Beats.\\nДобавить поле readiness для контроля жизненного цикла NPC.\\nПосле каждого обновления синхронизировать статус с `npc-status-tracker`.\\n", "mechanics_links": [], "assets": []}, {"id": "checklist", "title": "Чек-лист синхронизации", "body": "- Определены приоритеты и владельцы.\\n- Согласован список с `main-quests-template-plan.md`.\\n- Требуется завершить обновление файлов NPC до статуса ready.\\n", "mechanics_links": [], "assets": []}, {"id": "follow_up", "title": "Следующие действия", "body": "Завершить подготовку Padre Ibarra и Hanako Arasaka до 2025-11-10.\\nПровести ревью приоритетных NPC 2025-11-11.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "NPC Important List", "link": "important-npcs-list.md"}, {"title": "NPC Template", "link": "NPC-TEMPLATE.md"}, {"title": "Main Quests Template Plan", "link": "../quests/main/main-quests-template-plan.md"}], "decisions": []}, "implementation": {"needs_task": false, "github_issue": 108, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-08", "author": "lore_keeper", "changes": "Матрица приоритизации NPC сформирована и согласована с планом основных квестов."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

