INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('canon-npc-ripperdoc-cassius-2060-2090', 'Cassius Ryder — Ripper Doc City Center (2060-2090)', '{"metadata": {"id": "canon-npc-ripperdoc-cassius-2060-2090", "title": "Cassius Ryder — Ripper Doc City Center (2060-2090)", "document_type": "canon", "category": "npc", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-06T11:32:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-06T11:32:00+00:00", "owners": [{"role": "npc_lore_lead", "contact": "lore@necp.game"}], "tags": ["npc", "ripperdoc", "city-center"], "topics": ["npc-lore", "character-development"], "related_systems": ["narrative-service", "high-end-economy"], "related_documents": [{"id": "canon-location-city-center-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/ripperdocs/cassius-ryder-2060-2090.md", "visibility": "internal", "audience": ["narrative", "lore", "vision"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль Кассиуса Райдера существовал в Markdown и не был интегрирован в каталог знаний NPC.", "goal": "Сформировать структурированное описание элитного ripper doc для использования в сюжетах и экономических арках.", "essence": "Документ раскрывает историю, характер и сюжетные крючки Кассиуса Райдера в период 2060–2090 годов.", "key_points": ["Cassius работает исключительно с корпоративной элитой и использует премиум-оборудование.", "Персонаж демонстрирует холодный профессионализм и высокие стандарты обслуживания.", "Квест «Операция класса люкс» даёт доступ к лучшим имплантам через корпоративный контракт."]}, "content": {"sections": [{"id": "biography", "title": "История и позиционирование", "body": "Кассиус Райдер — самый дорогой ripper doc Найт-Сити, принимающий клиентов в частной клинике City Center.\\nРаботает с 2060 года, предоставляет премиальные импланты и поддерживает репутацию идеального хирурга корпоратов.\\n", "mechanics_links": [], "assets": []}, {"id": "personality", "title": "Личность", "body": "Высокомерный, перфекционист и требовательный к клиентам.\\nОтказывает всем, кто не может оплатить услугу или не соответствует его стандартам.\\n", "mechanics_links": [], "assets": []}, {"id": "quests", "title": "Квестовые связи", "body": "«Операция класса люкс» — игрок получает доступ к клинике через корпоративный контракт и может установить лучшие импланты.\\n", "mechanics_links": [], "assets": []}, {"id": "dialogue", "title": "Характерные реплики", "body": "> \\"Мои услуги стоят дорого, потому что ошибок не бывает.\\"\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "City Center District Guide", "link": "../../../../scenarios/locations/night-city/city-center-2020-2093.md"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-06", "author": "lore_team", "changes": "Создан профиль Кассиуса Райдера с описанием квеста и характера."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

