INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('canon-npc-ripperdoc-vik-2030-2090', 'Виктор "Vik" Вектор — Ripper Doc Watson (2030-2090)', '{"metadata": {"id": "canon-npc-ripperdoc-vik-2030-2090", "title": "Виктор \\"Vik\\" Вектор — Ripper Doc Watson (2030-2090)", "document_type": "canon", "category": "npc", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-06T11:28:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-06T11:28:00+00:00", "owners": [{"role": "npc_lore_lead", "contact": "lore@necp.game"}], "tags": ["npc", "ripperdoc", "watson"], "topics": ["npc-lore", "character-development"], "related_systems": ["narrative-service", "quest-system"], "related_documents": [{"id": "canon-location-watson-2020-2093", "relation": "references"}, {"id": "canon-npc-prioritization-matrix", "relation": "aligns"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/ripperdocs/viktor-vektor-2030-2090.md", "visibility": "internal", "audience": ["narrative", "lore", "vision"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль Виктора Вектора существовал в Markdown и не был подключён к базе знаний NPC.", "goal": "Структурировать данные о ключевом ripper doc Watson для использования в квестах и нарративных решениях.", "essence": "Документ описывает историю, личность, квесты и цитаты Виктора Вектора в период 2030–2090 годов.", "key_points": ["Виктор — легендарный ripper doc Watson, работающий по доверию и в долг.", "Основные квесты включают возвращение оборудования и установку сложного импланта.", "Персонаж служит моральным ориентиром и поддерживает связи с героями 2077."]}, "content": {"sections": [{"id": "biography", "title": "История и контекст", "body": "Виктор Вектор — бывший профессиональный боксёр, ставший ripper doc и легендой Watson.\\nРаботает в подвале Misty''s Esoterica, помогает соседям и часто лечит в долг тех, кому доверяет.\\nВ 2077 пытался спасти V от биочипа Релика, но столкнулся с технологическим пределом.\\n", "mechanics_links": [], "assets": []}, {"id": "personality", "title": "Личность", "body": "Добрый, честный и надёжный наставник, сохраняющий человечность среди кибернетики.\\nОтцовская фигура для многих жителей района и источник советов для игроков.\\n", "mechanics_links": [], "assets": []}, {"id": "quests", "title": "Квестовые связи", "body": "- «Старый боксёр»: возврат украденного оборудования клиники.\\n- «Золотые руки»: установка уникального импланта, от которого отказались другие ripper doc.\\n", "mechanics_links": [], "assets": []}, {"id": "dialogue", "title": "Характерные реплики", "body": "> \\"Импланты — это инструмент. Главное — кто ими пользуется.\\"\\n\\n> \\"Я видел, как люди превращаются в киборгов и теряют человечность. Не дай этому случиться с тобой, kid.\\"\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Watson District Guide", "link": "../../../../scenarios/locations/night-city/watson-2020-2093.md"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-06", "author": "lore_team", "changes": "Создан профиль Виктора Вектора с квестовыми связями и ключевыми цитатами."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

