INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-solo-morgan-blackhand-1990-2023', 'Морган Блэкхэнд — Legendary Solo (1990–2023)', '{"metadata": {"id": "npc-solo-morgan-blackhand-1990-2023", "title": "Морган Блэкхэнд — Legendary Solo (1990–2023)", "document_type": "canon", "category": "narrative", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-06T11:56:00+00:00", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "solo", "legend"], "topics": ["mercenary-culture", "corporate-wars"], "related_systems": ["narrative-service", "quest-service"], "related_documents": [{"id": "scenarios-fourth-corporate-war-2020-2023", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/solo/morgan-blackhand-legend-1990-2023.md", "visibility": "internal", "audience": ["narrative", "concept", "quest-design"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужно зафиксировать легенду Solo, определяющую эталон для наёмников Night City.", "goal": "Систематизировать биографию, слухи и игровые применения Моргана Блэкхэнда.", "essence": "Легендарный Solo времён корпоративных войн, пропавший после взрыва Arasaka Tower, остаётся символом профессиональной чести.", "key_points": ["Активен 1990–2023 годы, участвовал в Четвёртой корпоративной войне.", "Пропал без вести, вокруг статуса множество слухов.", "Используется в квестах как ориентир для кодекса Solo."]}, "content": {"sections": [{"id": "legend-overview", "title": "Легендарная биография", "body": "Морган Блэкхэнд — символ профессионального Solo. Участвовал в десятках корпоративных войн, выбирая сторону по\\nсобственным принципам. Во время штурма Arasaka Tower в 2023 году пропал без вести, что породило мифы о его\\nвозможном выживании. Легенды рассказывают о сотнях побед и отказах от выгодных контрактов ради чести.\\n", "mechanics_links": ["mechanics/combat/combat-overview.yaml"], "assets": []}, {"id": "personality", "title": "Личность и кодекс", "body": "Блэкхэнд известен дисциплиной, прагматизмом и жёстким кодексом. Он уважает тех, кто способен довести миссию до\\nконца, и презирает предательство. Для многих Solo его имя стало синонимом профессионализма.\\n", "mechanics_links": [], "assets": []}, {"id": "quest-hooks", "title": "Квестовые хуки", "body": "Квест \\"Призрак Blackhand\\" отправляет игроков на поиски следов легенды: жив ли Морган или его имя используют для\\nманипуляций? Варианты финала включают встречу с настоящим героем, раскрытие его смерти или разоблачение подделки.\\n", "mechanics_links": ["quest/main/quest-main-2085-blackwall-expansion.yaml"], "assets": []}, {"id": "dialogue", "title": "Иконографические цитаты", "body": "> \\"Solo — это не тот, кто убивает. Это тот, кто выживает.\\"\\n", "mechanics_links": [], "assets": []}, {"id": "mythos", "title": "Мифология и слухи", "body": "Легенды утверждают, что Блэкхэнд отказался от состояния ради принципов, а также что он скрывается от корпораций.\\nЭти слухи поддерживают интерес улиц и квестовых цепочек, где игроки сталкиваются с последствиями его действий.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_director", "changes": "Конвертация легенды Моргана Блэкхэнда из MD в YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

