INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-ripperdoc-fingers-2050-2080', 'Финнеган «Fingers» Маркуорт — риппердок Джапантауна', '{"metadata": {"id": "npc-ripperdoc-fingers-2050-2080", "title": "Финнеган «Fingers» Маркуорт — риппердок Джапантауна", "document_type": "canon", "category": "npc-lore", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-09T04:42:00+00:00", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "ripperdoc", "japantown"], "topics": ["npc_profiles", "night_city_culture"], "related_systems": ["narrative-service", "world-simulation"], "related_documents": [{"id": "location-japantown-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/ripperdocs/fingers-2050-2080.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "2025-11-09T04:42:00+00:00", "status": "approved"}], "next_actions": []}, "summary": {"problem": "Требуется зафиксировать каноническое описание спорного риппердока Fingers для нарративных арок Night City.", "goal": "Стандартизировать профиль NPC с ключевыми чертами, мотивацией и связями для последующей интеграции в квесты и события.", "essence": "Fingers — дешёвый и аморальный риппердок Джапантауна, чьи услуги и влияние порождают моральные дилеммы для игроков.", "key_points": ["Обслуживает уязвимые группы и эксплуатационно относится к клиентам.", "Связан с сюжетной линией Evelyn Parker и конфликтами с Mox.", "Предлагает дешёвые импланты с высоким риском осложнений."]}, "content": {"sections": [{"id": "profile", "title": "Профиль и базовые сведения", "body": "Финнеган «Fingers» Маркуорт работает на Jig-Jig Street в Джапантауне и славится дешёвыми, но небезопасными процедурами. Мужчина преклонного возраста с сомнительной репутацией, эксплуатирующий уязвимых работников индустрии удовольствий и криминальные элементы.\\n", "mechanics_links": [], "assets": []}, {"id": "history", "title": "История и контекст", "body": "С 2050 года Fingers обосновался в Джапантауне, предлагая бюджетный кибервар людям, не имеющим доступа к официальным клиникам. В 2077 году вовлечён в кейс Evelyn Parker, что закрепило его репутацию насильника доверия. Отношения с бандой Mox напряжённые: они презирают врача, но вынуждены иногда терпеть его из-за нужд своих работников.\\n", "mechanics_links": [], "assets": []}, {"id": "personality", "title": "Личность и поведение", "body": "Характер — слизкий, трусливый и алчный. Fingers не задаёт вопросов, ориентируется на быстрый заработок и не чувствует ответственности за последствия имплантов. Общается снисходительно, быстро меняет тон при угрозе.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровые импликации", "body": "Квестовый узел «Грязные руки» позволяет игрокам получить дешёвые импланты с негативными модификаторами. Решения вокруг Fingers формируют моральные развилки и влияют на отношения с Mox и секс-работниками Джапантауна.\\n", "mechanics_links": ["mechanics/social/npc-hiring-management.yaml", "mechanics/progression/progression-attributes-matrix.yaml"], "assets": []}, {"id": "quotes", "title": "Цитаты", "body": "- «Я не задаю вопросов. Ты платишь, я делаю.»\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Jig-Jig Street", "definition": "Район развлечений Джапантауна, где действует Fingers."}], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертировано описание NPC Fingers в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

