INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-common-enemy-chameleon-merc-1832', 'Наёмник-«хамелеон» (маскировка под окружение)', '{"metadata": {"id": "npc-common-enemy-chameleon-merc-1832", "title": "Наёмник-«хамелеон» (маскировка под окружение)", "document_type": "canon", "category": "narrative", "status": "draft", "version": "1.0.0", "last_updated": "2025-12-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "enemy", "stealth", "sniper"], "topics": ["ambush", "stealth"], "related_systems": ["narrative-service", "gameplay-service", "ai-service", "analytics-service"], "related_documents": [{"id": "mechanics-combat-ai-enemies", "relation": "references"}, {"id": "mechanics-combat-combat-stealth", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/enemies/chameleon-merc.md", "visibility": "internal", "audience": ["concept", "narrative", "gameplay"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужен профиль наёмника с активной маскировкой: засады, смена позиции, уязвимость к термо/шум-сканам.", "goal": "Зафиксировать параметры камуфляжа, кд, ловушек/мин и телеметрию.", "essence": "Засадный стрелок с камуфляжем: выстрел — уход, ловушки на подходах, раскрывается сканами.", "key_points": ["Камуфляж скрывает до выстрела, охлаждающий таймер.", "Ловушки/мины на тропах; смена позиции после выстрела.", "Работает со снайперами-дронами, подсвечивает цели.", "Телеметрия: обнаружение сканами, убийства из засады."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Наёмник с активной маскировкой, действует из засады. Вступает в бой короткими сериями, затем меняет позицию.\\nКамуфляж сбрасывается при выстреле и требует охлаждения. Использует ловушки, чтобы задержать преследующих.\\n", "mechanics_links": [], "assets": []}, {"id": "abilities", "title": "Способности", "body": "• Камуфляж: до выстрела, раскрытие на 2–3с после выстрела; охлаждение 10–16с.  \\n• Ловушки/мины: 1–2 устройства на подходах; замедление/шок. Кд 18–26с.  \\n• Маркер цели: слабая метка для отряда/дронов, кд 14–20с.  \\n• Быстрый выстрел + отход: короткая очередь/одиночный выстрел, сразу смена позиции.\\n", "mechanics_links": [], "assets": []}, {"id": "behavior", "title": "Поведение", "body": "• Ждёт жертву, открывает огонь, уходит в маскировку после кд и смещается на новую точку.  \\n• Обходит сенсоры; если сканеров много — ставит мину и отступает.  \\n• Предпочитает высоту/диагональные углы, избегает длительных перестрелок.\\n", "mechanics_links": [], "assets": []}, {"id": "counters", "title": "Контр-игра", "body": "• Термо/шум-сканы раскрывают камуфляж; импульсные гранаты сбивают маску.  \\n• Агрессивный пуш после выстрела, пока камуфляж в кд.  \\n• Детект ловушек сканерами, обход или подрыв заранее.\\n", "mechanics_links": [], "assets": []}, {"id": "telemetry", "title": "Телеметрия", "body": "• detection_by_scan_rate, ambush_kills, reposition_after_shot_time.  \\n• trap_trigger_rate, cloak_uptime, first_shot_time.  \\n• Цели: cloak_uptime 35–50%, reposition ≤5с после выстрела, ambush_kills 0.3–0.6/бой.\\n", "mechanics_links": [], "assets": []}, {"id": "loot", "title": "Лут", "body": "• Модули камуфляжа, метки, ловушки; редкий шанс на термо-щит.  \\n• Данные о маршрутах (низкая ценность), иногда — квестовый ключ.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1832, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-12-12", "author": "content_writer", "changes": "Создан профиль наёмника-хамелеона с камуфляжем, ловушками и телеметрией."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

