INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-common-enemy-cyberhound-tracker-1824', 'Киберпсы-трекеры (scent tag)', '{"metadata": {"id": "npc-common-enemy-cyberhound-tracker-1824", "title": "Киберпсы-трекеры (scent tag)", "document_type": "canon", "category": "narrative", "status": "draft", "version": "1.0.0", "last_updated": "2025-12-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "enemy", "cyberhound", "tracker"], "topics": ["tracking", "melee"], "related_systems": ["narrative-service", "gameplay-service", "ai-service", "analytics-service"], "related_documents": [{"id": "mechanics-combat-ai-enemies", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/enemies/cyberhound-tracker.md", "visibility": "internal", "audience": ["concept", "narrative", "gameplay"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужен профиль киберпсов-трекеров с scent-tag меткой, агро-баффом стаи и уклонениями.", "goal": "Зафиксировать длительность метки, бонус агро/точности, кд, прыжки/уклонения и телеметрию.", "essence": "Быстрые трекеры: метят цель, повышают агро/точность союзников, прыгают и уклоняются; контрятся хаком/глушителем.", "key_points": ["Метка 20–30с, бафф агро/точности по цели.", "Приоритет хил/нетраннеры, смена цели после кд метки.", "Уклонения при фокусе, отступление при потере стаи.", "Телеметрия: смертность целей с меткой, снятия метки."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Стая киберпсов, заточенных под трекинг. Выдаёт scent-tag на ключевых целях, повышая агрессию и точность пакета.\\nРаботают быстро, используют прыжки и уклонения, уходят при потере большинства стаи.\\n", "mechanics_links": [], "assets": []}, {"id": "abilities", "title": "Способности", "body": "• Scent tag: метка 20–30с, бафф агро/точности союзников по цели; кд 12–18с.  \\n• Прыжок/уклонение: короткие прыжки, спам уклонений при фокусе; кд 6–10с.  \\n• Укус ближки, слабый bleed.  \\n• Снятие метки: хак/глушитель снимает; повторная метка после кд.\\n", "mechanics_links": [], "assets": []}, {"id": "behavior", "title": "Поведение", "body": "• Приоритет цели — хил/нетраннер.  \\n• Смена цели после отката метки или при потере стаи.  \\n• Отступление при потере большей части стаи; спам уклонений под фокусом.\\n", "mechanics_links": [], "assets": []}, {"id": "counters", "title": "Контр-игра", "body": "• Глушитель/хак для снятия метки.  \\n• АОЕ/шок по стае, чтобы сбить агро.  \\n• Удерживать дистанцию, ловушки на маршрутах.\\n", "mechanics_links": [], "assets": []}, {"id": "telemetry", "title": "Телеметрия", "body": "• tag_duration_avg, agro_bonus_effect, accuracy_bonus_effect.  \\n• tagged_target_deaths, tag_removed_rate, dodge_events.  \\n• Цели: tag_removed_rate ≥30% подготовленными игроками; agro/accuracy бонусы в целевом коридоре.\\n", "mechanics_links": [], "assets": []}, {"id": "loot", "title": "Лут", "body": "• Метки, сенсорные модули; редкий шанс на scent-сканер.  \\n• Низкоценные батареи.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1824, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-12-12", "author": "content_writer", "changes": "Создан профиль киберпсов-трекеров с scent-tag, уклонениями и телеметрией."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

