-- NPC: npc-common-enemy-cleanup-mech-suppressor-1830
INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-common-enemy-cleanup-mech-suppressor-1830', 'Уборочный мех-подавитель (дым/химия)', '{"metadata": {"id": "npc-common-enemy-cleanup-mech-suppressor-1830", "title": "Уборочный мех-подавитель (дым/химия)", "document_type": "canon", "category": "narrative", "status": "draft", "version": "1.0.0", "last_updated": "2025-12-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "enemy", "mech", "suppression"], "topics": ["stealth-counter", "area-denial"], "related_systems": ["narrative-service", "gameplay-service", "ai-service", "analytics-service"], "related_documents": [{"id": "mechanics-combat-ai-enemies", "relation": "references"}, {"id": "mechanics-combat-combat-stealth", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/enemies/cleanup-mech-suppressor.md", "visibility": "internal", "audience": ["concept", "narrative", "gameplay"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужен профиль меха, который срывает стелс дымом/химией, перетаскивает тела и ломает рейтинги стелса.", "goal": "Зафиксировать параметры дыма/DoT, скорость перетаскивания тел, кд и телеметрию.", "essence": "Стелс-контр: дым/хим облака, перенос тел в свет, фокус турелей/роев по ослеплённым.", "key_points": ["Дым/хим-облака снижают видимость и дают слабый DoT.", "Тянет тела в свет, поднимая тревогу.", "Уязвим к взлому/EMP.", "Телеметрия: сорванные стелс-прохождения, убийства под дымом."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Полуутилитарный мех, модифицированный под подавление стелса. Бросает дым/химию, тянет тела в освещённые зоны,\\nзаставляя охрану поднимать тревогу. Сам избегает фронтового боя, держится позади пачки. Масса ~420 кг, броня\\nкомпозит, уязвим к EMP/электро. Скорость 3.6–4.2 м/с (без груза).\\n", "mechanics_links": [], "assets": []}, {"id": "abilities", "title": "Способности", "body": "• Дым/хим-облако: радиус 8–12 м, длительность 8–12с, слабый DoT 3–6 HP/тик; кд 14–22с; накладывает «blind» (-35% точность на 6с).  \\n• Перетаскивание тела: скорость 1.6–2.2 м/с, тянет в освещённую зону; может сбросить при уроне >12% HP за тик; при успехе повышает тревогу.  \\n• Маркер «грязной зоны»: помечает область для турелей/роев, даёт им +15–25% точности и +10% урона по «blind».  \\n• Самозащита: короткий электрошок/спрей, кд 10–14с; накладывает mini-stun 0.4с на ближней дистанции.\\n", "mechanics_links": [], "assets": []}, {"id": "behavior", "title": "Поведение", "body": "• Заходит на точки стелса, бросает дым, вытягивает тела на свет.  \\n• Избегает прямого боя, держится за спинами; при фокусе отступает и зовёт подкреп.  \\n• Синхронизируется с турелями/роями для фокуса по ослеплённым целям; при отсутствии целей патрулирует трупы.\\n", "mechanics_links": [], "assets": []}, {"id": "counters", "title": "Контр-игра", "body": "• Взлом/EMP, чтобы отключить меха; фокус после броска дыма.  \\n• Сохранение тел в закрытых зонах, быстрый лут/эвак.  \\n• Сканеры/тепловизоры, чтобы отслеживать маршруты и закладывать ловушки.  \\n• Пульс гранаты для сброса перетаскиваемого тела.\\n", "mechanics_links": [], "assets": []}, {"id": "telemetry", "title": "Телеметрия", "body": "• stealth_break_events, bodies_moved_to_light, dot_kills_under_smoke.  \\n• turret_focus_events, smoke_uptime, emp_shutdowns, blind_applied.  \\n• Цели: break_events в стелс-зонах ≤1/бой при осторожной игре; smoke_uptime 25–40%; emp_shutdowns ~1/бой при наличии хакера; blind_applied/encounter ≤5.\\n", "mechanics_links": [], "assets": []}, {"id": "loot", "title": "Лут", "body": "• Хим-картриджи, дымовые гранаты, слабые модули сенсоров.  \\n• Редкий шанс тех-данных о маршрутах уборки (квест).\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1830, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-12-12", "author": "content_writer", "changes": "Создан профиль уборочного меха-подавителя с дымом/химией, перетаскиванием тел и телеметрией."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

