-- NPC: npc-common-enemy-cyberpsycho-miniboss-1836
INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-common-enemy-cyberpsycho-miniboss-1836', 'Киберпсих (mini-boss)', '{"metadata": {"id": "npc-common-enemy-cyberpsycho-miniboss-1836", "title": "Киберпсих (mini-boss)", "document_type": "canon", "category": "narrative", "status": "draft", "version": "1.0.0", "last_updated": "2025-12-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "enemy", "cyberpsycho", "miniboss", "raid"], "topics": ["combat-elite", "boss-phases"], "related_systems": ["narrative-service", "gameplay-service", "ai-service", "analytics-service"], "related_documents": [{"id": "mechanics-combat-ai-enemies", "relation": "references"}, {"id": "mechanics-combat-combat-abilities", "relation": "references"}, {"id": "mechanics-combat-combat-shooting-advanced", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/enemies/cyberpsycho-miniboss.md", "visibility": "internal", "audience": ["concept", "narrative", "gameplay"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужен профиль мини-босса-киберпсиха с фазами ярости и замыкания, влиянием на тактику сквада и телеметрией.", "goal": "Зафиксировать фазы, урон/защиту, частоту замыканий, окна уязвимости и параметры телеметрии для баланса.", "essence": "Непредсказуемый элитный враг с рывками и самооглушениями, создающий пики угрозы и окна для контратаки.", "key_points": ["Две основные фазы: ярость (урон↑, защита↓) и замыкание (самостановка 1–2с).", "Рывки, прыжки, метательные лезвия; иммун к страху и контролю слабой силы.", "Переключает таргет между слабейшей и самой опасной целью; меняет позицию после рывка.", "Телеметрия: смертность игроков, длительность боя, окна дамага, частота замыканий."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Элита-человек с тяжёлым киберпсихозом. Играет от агрессии и рывков, ломает построения сквада,\\nвынуждает держать дистанцию и контролировать пространство. Опасен в ближнем бою и при метательных\\nатаках, но создаёт окна уязвимости через собственные глитчи.\\n", "mechanics_links": [], "assets": []}, {"id": "phases", "title": "Фазы и состояния", "body": "• Ярость: +30–45% урон, -20–30% броня, длительность 8–12с, кд 18–25с.  \\n• Замыкание: самостановка 1–2с каждые 20–30с, сбрасывает агро, открывает окно фокуса.  \\n• Порог HP-триггеры: при 70% и 35% HP получает усиленный рывок и шквал лезвий.  \\n• Иммун: страх, слабые станы; восприимчив к мощным шок/тяжёлому контролю.\\n", "mechanics_links": [], "assets": []}, {"id": "abilities", "title": "Способности", "body": "• Рывок-разрез: 12–18 м, кд 6–9с, наносит кровоток, сбивает укрытия низкого класса.  \\n• Прыжок с ударом: вертикальный вход, АоЕ 4–5 м, шанс нокдауна.  \\n• Метательные лезвия: веер 3–5 снарядов, шквал при HP <50%.  \\n• Глитч-усиление: при замыкании следующий удар +50% урон, после — усталость (урон -20% на 5с).\\n", "mechanics_links": [], "assets": []}, {"id": "ai-behavior", "title": "Поведение AI", "body": "• Выбор цели: чередует слабейшую по HP и наибольший DPS, чтобы рвать строй.  \\n• После 1–2 рывков меняет позицию, избегая удержания в одном углу.  \\n• При дальнем кайте ускоряет вход через прыжок+рывок; при ближнем контакте — чередует лезвия и захваты.  \\n• Не боится численного преимущества, но реагирует на тяжёлый контроль — отскакивает или уходит в дым.\\n", "mechanics_links": [], "assets": []}, {"id": "counters", "title": "Контр-игра", "body": "• Шок/тяжёлый контроль во время замыкания даёт безопасное окно; лёгкие станы бесполезны.  \\n• Кайте и коридоры: заставить тратить рывок в пустоту, затем фокусить при кд 6–9с.  \\n• Укрытия высокого класса и высота снижают эффективность прыжков; турели с шоком ускоряют усталость.  \\n• Анти-ближка: минные ловушки и оглушающие гранаты перед рывком.\\n", "mechanics_links": [], "assets": []}, {"id": "telemetry", "title": "Телеметрия и баланс", "body": "• player_deaths_per_fight, avg_fight_duration, damage_taken_windows (во время замыкания).  \\n• frenzy_uptime, frenzy_damage_bonus, self_stun_frequency.  \\n• dash_whiff_rate, blade_hit_rate, knockdown_events.  \\n• Цели: длительность боя 65–110с для сквада 3–4; окна урона ≥5с каждые 20–30с; смертность игроков ≤35% при равной силе.\\n", "mechanics_links": [], "assets": []}, {"id": "loot", "title": "Лут и награды", "body": "• Имплант-фрагменты, редкие моды на ближний бой, редкий шанс чертежа метательных лезвий.  \\n• Низкий шанс данных о киберпсихозе (квестовый хук), кредиты среднего тира.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1836, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-12-12", "author": "content_writer", "changes": "Создан профиль мини-босса киберпсиха с фазами ярости и замыкания, тактикой и телеметрией."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

