INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-common-enemy-corpo-assault-squad-1831', 'Корпо-штурмовой отряд (щит + тактическая координация)', '{"metadata": {"id": "npc-common-enemy-corpo-assault-squad-1831", "title": "Корпо-штурмовой отряд (щит + тактическая координация)", "document_type": "canon", "category": "narrative", "status": "draft", "version": "1.0.0", "last_updated": "2025-12-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "enemy", "squad", "shield", "tactical"], "topics": ["coordinated-combat", "corporate"], "related_systems": ["narrative-service", "gameplay-service", "ai-service", "analytics-service"], "related_documents": [{"id": "mechanics-combat-ai-enemies", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/enemies/corpo-assault-squad.md", "visibility": "internal", "audience": ["concept", "narrative", "gameplay"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужен профиль корп-штурмового стека с координацией ролей, щитовиками и метками целей.", "goal": "Зафиксировать параметры щитов, метки, кулдауны дым/флеш и телеметрию kill-time/маркировки.", "essence": "Скоординированный стек: щиты закрывают фронт, штурмовики флангуют, снайпер маркирует, лидер даёт команды.", "key_points": ["Щитовики перекрывают фронт и гранаты; штурмовики флангуют.", "Маркеры целей по ролям (медик/нетраннер) дают бафф урона.", "Дым/флеш для входа; смена направления при потерях.", "Телеметрия: kill-time, эффективность маркировки, смены команд."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Корпоративный штурмовой стек с чёткой ролевой структурой. Лидер командует, щитовики держат фронт, штурмовики\\nфлангуют, снайпер маркирует цели. Высокая скоординированность, быстрая адаптация к потерям.\\n", "mechanics_links": [], "assets": []}, {"id": "abilities", "title": "Способности и снаряжение", "body": "• Щиты: блок фронта, поглощение 1200–1600 урона, кд поднятия 6–9с, частичный блок гранат.  \\n• Маркер цели: снайпер даёт метку на 8–12с, +15–25% урон от отряда по цели; кд 14–20с.  \\n• Дым/флеш: для входа и смены фланга; кд 16–22с.  \\n• Команды лидера: push/hold/retreat, переключают поведение штурмовиков; кд смены 10–14с.\\n", "mechanics_links": [], "assets": []}, {"id": "behavior", "title": "Поведение", "body": "• Старт: щиты вперёд, дым на вход, штурмовики обходят.  \\n• При потере двух бойцов — смена направления фланга.  \\n• Снайпер маркирует приоритет (медик/нетраннер), штурм фокусит цель.  \\n• Лидер переключает команды по ситуации; оборона — компакт, атака — сплит по флангам.\\n", "mechanics_links": [], "assets": []}, {"id": "counters", "title": "Контр-игра", "body": "• EMP/шок против щитов, фокус по снайперу/лидеру для срыва команд.  \\n• Дым/ослепление ответа, растягивание флангов, мины на маршрутах.  \\n• Разрыв дистанции, чтобы щиты теряли ценность, и быстрый фокус по меткам.\\n", "mechanics_links": [], "assets": []}, {"id": "telemetry", "title": "Телеметрия", "body": "• squad_kill_time, mark_effectiveness, shield_damage_blocked.  \\n• command_switch_events, flank_success_rate, grenade_blocks.  \\n• Цели: kill_time 18–32с на равных, mark_effectiveness 12–20% доп. урона, flank_success ~50%.\\n", "mechanics_links": [], "assets": []}, {"id": "loot", "title": "Лут", "body": "• Щит-модули, тактические гранаты, редкий чертёж на маркер.  \\n• Корп-креды среднего/высокого тира, данные о протоколах.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1831, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-12-12", "author": "content_writer", "changes": "Создан профиль корп-штурмового отряда с ролями, метками и телеметрией."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

