INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-common-enemy-engineer-mech-support-1823', 'Инженерный мех поддержки', '{"metadata": {"id": "npc-common-enemy-engineer-mech-support-1823", "title": "Инженерный мех поддержки", "document_type": "canon", "category": "narrative", "status": "draft", "version": "1.0.0", "last_updated": "2025-12-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "enemy", "mech", "support"], "topics": ["repair", "turrets"], "related_systems": ["narrative-service", "gameplay-service", "ai-service", "analytics-service"], "related_documents": [{"id": "mechanics-combat-ai-enemies", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/enemies/engineer-mech-support.md", "visibility": "internal", "audience": ["concept", "narrative", "gameplay"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужен профиль меха-ремонтника: чинит, ставит мини-турели и даёт EMP-щит.", "goal": "Зафиксировать параметры ремонта, щита, турелей, кд и телеметрию.", "essence": "Тыловой саппорт-мех: восстанавливает броню, разворачивает мини-турели, бафает союзников EMP-щитом.", "key_points": ["Ремонт укрытий/шагоходов (20–30% брони).", "1–2 мини-турели с адаптацией урона.", "EMP-щит 5с снижает урон и гасит сигналы.", "Телеметрия: длительность боя, эффективность турелей."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Мех поддержки, держится за спинами танков и шагоходов. Чинит броню, ставит мини-турели и даёт краткий EMP-щит,\\nснижая входящий урон и усложняя взлом рядом.\\n", "mechanics_links": [], "assets": []}, {"id": "abilities", "title": "Способности", "body": "• Ремонт: восстанавливает 20–30% брони укрытий/шагоходов/союзников; кд 14–20с.  \\n• Мини-турели: 1–2 шт, адаптация урона, HP/DPS среднего тира; кд развёртывания 18–26с.  \\n• EMP-щит: 5с, снижает входящий урон союзникам в радиусе, гасит сигналы; кд 22–30с.  \\n• Поддержка роев/турелей: баф точности/скорострельности на 10–18%.\\n", "mechanics_links": [], "assets": []}, {"id": "behavior", "title": "Поведение", "body": "• Держится за танками/шагоходами, отступает при фокусе, ставит щит.  \\n• При угрозе ближке — выпускает турели, зовёт рой/киберпсов.  \\n• Чинит в безопасных окнах, прижатие — щит или отход.\\n", "mechanics_links": [], "assets": []}, {"id": "counters", "title": "Контр-игра", "body": "• Фокус до развёртывания турелей; EMP/взлом по щиту.  \\n• Снести турели/рои, лишив баффов.  \\n• Давление и фланг, чтобы не дать ремонтировать.\\n", "mechanics_links": [], "assets": []}, {"id": "telemetry", "title": "Телеметрия", "body": "• repair_amount, shield_uptime, turret_dps_effective, fight_duration_delta.  \\n• dps_with_mech_vs_without, emp_block_events, turret_adapt_switches.  \\n• Цели: заметное удлинение боя, но контримое фокусом/EMP.\\n", "mechanics_links": [], "assets": []}, {"id": "loot", "title": "Лут", "body": "• Запчасти, турельные модули, батареи; редкий чертёж EMP-щита.  \\n• Логи ремонтных операций (низкая ценность).\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1823, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-12-12", "author": "content_writer", "changes": "Создан профиль инженерного меха с ремонтом, мини-турелями, EMP-щитом и телеметрией."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

