-- NPC: npc-common-enemy-field-medic-responder-1835
INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-common-enemy-field-medic-responder-1835', 'Боевой медик-полевик (реаниматор)', '{"metadata": {"id": "npc-common-enemy-field-medic-responder-1835", "title": "Боевой медик-полевик (реаниматор)", "document_type": "canon", "category": "narrative", "status": "draft", "version": "1.0.0", "last_updated": "2025-12-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "enemy", "medic", "support", "ai"], "topics": ["combat-support", "squad-synergy"], "related_systems": ["narrative-service", "gameplay-service", "ai-service", "analytics-service"], "related_documents": [{"id": "mechanics-combat-ai-enemies", "relation": "references"}, {"id": "mechanics-combat-combat-abilities", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/enemies/field-medic-responder.md", "visibility": "internal", "audience": ["concept", "narrative", "gameplay"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужен профиль вражеского медика, который поднимает союзников под прикрытием дыма/щита и меняет динамику боя.", "goal": "Зафиксировать механику реанимации с каналом, ограничения зарядов, поведение под фокусом и телеметрию для баланса.", "essence": "Поддерживающий юнит дальнего хила и реанима, играющий от укрытий и дымовых экранов, с ограниченными ресурсами и уязвимостью к прерыванию каналов.", "key_points": ["Реанимация через канал 2.5–3.5с под дымом/переносным щитом; 2–3 заряда.", "Дистанционное лечение короткими сериями, кд 8–12с, радиус 15–20 м.", "Поведение: держится в тылу, выходит только под поднятие downed, при фокусе кидает дым и отступает.", "Телеметрия: успешные/прерванные реанимы, средний хил за жизнь, использование дымов/щита."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Поддерживающий боец, сопровождает отряд и держится за укрытиями. Главная задача — поднимать союзников\\nи заливать быстрый хил для удержания флангов. Не вступает в фронтальный бой, предпочитает смену позиции\\nпосле каждого раскрытия.\\n", "mechanics_links": [], "assets": []}, {"id": "role-and-behavior", "title": "Роль и поведение", "body": "• Триггер выхода — появление downed-союзника в радиусе 25 м.  \\n• Перед реанимом разворачивает дымовую гранату или переносной проекционный щит, затем начинает канал.  \\n• При получении фокуса >30% HP за 2 секунды — бросает второй дым/отступает за укрытие, прерывая действие.  \\n• Патрулирует заднюю линию, периодически меняет позицию после раскрытия, чтобы избежать повторного фокуса.\\n", "mechanics_links": [], "assets": []}, {"id": "abilities", "title": "Способности", "body": "• Реаним-канал: 2.5–3.5с, 2–3 заряда на бой; ставит дым 6–8с и проекционный щит 900–1100 HP, кд 18–24с.  \\n• Дистанционный хил: импульсный луч 3 тика по 35–45 HP, кд 8–12с, радиус 15–20 м; 4–5 стаков на бой.  \\n• Дымовая отвязка: мгновенный дым на себя при фокусе, кд 25–30с, снижает точность противников на 15–20%.  \\n• Самозащита: слабый пистолет, низкий урон; цель — удержать дистанцию, а не убивать.\\n", "mechanics_links": [], "assets": []}, {"id": "tactics-and-synergy", "title": "Тактика и синергии", "body": "• Работает в паре с лидером/щитовиком, который открывает окно для реанима.  \\n• Ставит дым по линии обзора на downed, вынуждая игроков сближаться и рисковать минами/кроссфайром.  \\n• Снайперы и роумеры прикрывают канал; медик использует их метки, чтобы избегать опасных углов.  \\n• В штурме держится на средне-дальней дистанции; в обороне — рядом с точками возрождения и укрытиями.\\n", "mechanics_links": [], "assets": []}, {"id": "counters", "title": "Контр-игра", "body": "• Прерывания: любые стан/шок/кнокаут сбивают канал реанима и тратят заряд.  \\n• Анти-дым: термо/ИК-прицелы, импульсные гранаты, взрывы, давящие щит.  \\n• Фокус по таймингу: заход после первого дыма, пока кд 18–24с, или кайт на открытом пространстве.  \\n• Превентивный урон: добивать downed до входа медика в радиус или ставить мины у тел.\\n", "mechanics_links": [], "assets": []}, {"id": "telemetry-and-balance", "title": "Телеметрия и баланс", "body": "• success_revives / interrupted_revives / failed_entries.  \\n• heal_done_per_life, heal_per_downed_target, average_channel_time.  \\n• smoke_usage_rate, shield_break_time, distance_to_downed_on_start.  \\n• Целевые коридоры: успех реанима 35–50% при равных складах, средний хил 350–500 HP за жизнь,\\n  прерывания ≥40% в высокоуровневых боях; использовать для автотюнинга кулдаунов/зарядов.\\n", "mechanics_links": [], "assets": []}, {"id": "loot-and-rewards", "title": "Лут и награды", "body": "• Мед-стимы, дымовые гранаты, редкий шанс тактического щита.  \\n• Данные о лечении (низкая ценность), иногда — квестовый меджек для побочек на спасение/эвакуацию.  \\n• Баланс: лут смещён в утилити, боевой лут минимален.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1835, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-12-12", "author": "content_writer", "changes": "Создан профиль боевого медика-полевика с параметрами реанимации, дымом и телеметрией."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

