INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-common-enemy-gang-berserker-1833', 'Ганг-берсерк (хим-стим + агрессия)', '{"metadata": {"id": "npc-common-enemy-gang-berserker-1833", "title": "Ганг-берсерк (хим-стим + агрессия)", "document_type": "canon", "category": "narrative", "status": "draft", "version": "1.0.0", "last_updated": "2025-12-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "enemy", "berserker", "stim"], "topics": ["close-combat", "gang"], "related_systems": ["narrative-service", "gameplay-service", "ai-service", "analytics-service"], "related_documents": [{"id": "mechanics-combat-ai-enemies", "relation": "references"}, {"id": "mechanics-combat-combat-abilities", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/enemies/gang-berserker.md", "visibility": "internal", "audience": ["concept", "narrative", "gameplay"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужен профиль уличного берсерка на хим-стиме: быстрый раш, высокий урон в ближке, уязвим к контролю.", "goal": "Зафиксировать параметры стима, урона, паники, ломания укрытий и телеметрию.", "essence": "Стим-агрессор: разгон в ближку, тикающий самоурон, паника без стима, ломает укрытия низкого класса.", "key_points": ["Стим даёт скорость/урон, тикает по самому берсерку.", "Рывок по кратчайшему пути, игнор огня до критического HP.", "При кд стима — паника/отход; ломает слабые укрытия.", "Телеметрия: смертность берсерков, урон по игрокам, частота паники."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Уличный боец на хим-стиме, специализируется на агрессивном заходе в ближку. Считает своё тело расходником,\\nполучает тикающий самоурон, пока действует стим. Опасен в первые секунды контакта, но быстро выдыхается.\\n", "mechanics_links": [], "assets": []}, {"id": "abilities", "title": "Способности и стим", "body": "• Хим-стим: +20–35% скорость, +25–40% урон ближки, длительность 10–14с, тикает по 2–4% HP/с; кд 18–26с.  \\n• Рывок: 8–12 м в цель, кд 6–9с; сбивает лёгкие укрытия.  \\n• Паника: при отсутствии стима и HP <30% — отход, шанс бросить дым.  \\n• Урон по укрытиям: ломает низкие/средние укрытия, оставляет проломы.\\n", "mechanics_links": [], "assets": []}, {"id": "behavior", "title": "Поведение", "body": "• Рашит по кратчайшему пути, игнорирует огонь до критического HP.  \\n• После выгорания стима ищет укрытие/бустер, может паниковать и отойти.  \\n• Если есть «бард»/ди-джей, синхронизирует стим под бафф волн.\\n", "mechanics_links": [], "assets": []}, {"id": "counters", "title": "Контр-игра", "body": "• Тяжёлый контроль/шок, чтобы сбить стим и рывок.  \\n• Кайты и высокие укрытия, которые он не ломает.  \\n• Фокус по таймеру кд стима; сжечь его ХП тиками.\\n", "mechanics_links": [], "assets": []}, {"id": "telemetry", "title": "Телеметрия", "body": "• berserk_uptime, self_dot_damage, rush_success_rate.  \\n• player_damage, panic_triggers, cover_break_events.  \\n• Цели: rush_success 35–50% на равных, panic ≥1 при долгом бою, self_dot ~20–30% HP за цикл.\\n", "mechanics_links": [], "assets": []}, {"id": "loot", "title": "Лут", "body": "• Хим-стимы, адренал, иногда чертёж бустера; утилити лут низкого-среднего тира.  \\n• Редкий шанс данных о синтезе стима (квест).\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1833, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-12-12", "author": "content_writer", "changes": "Создан профиль ганг-берсерка со стимом, рывком, паникой и телеметрией."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

