INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-common-enemy-hacker-illusionist-1838', 'Хакер-иллюзионист (ложные сигналы)', '{"metadata": {"id": "npc-common-enemy-hacker-illusionist-1838", "title": "Хакер-иллюзионист (ложные сигналы)", "document_type": "canon", "category": "narrative", "status": "draft", "version": "1.0.0", "last_updated": "2025-12-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "enemy", "hacker", "deception", "support"], "topics": ["combat-hacking", "sensory-warfare"], "related_systems": ["narrative-service", "gameplay-service", "ai-service", "analytics-service"], "related_documents": [{"id": "mechanics-combat-ai-enemies", "relation": "references"}, {"id": "mechanics-combat-combat-hacking", "relation": "references"}, {"id": "mechanics-combat-combat-hacking-combat-integration", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/enemies/hacker-illusionist.md", "visibility": "internal", "audience": ["concept", "narrative", "gameplay"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужен профиль врага, который создает ложные сигналы/голограммы и сбивает трекинг, отвлекая сквад.", "goal": "Зафиксировать параметры частоты и длительности фантомов, хаки HUD, поведение при раскрытии и телеметрию.", "essence": "Обман и дезориентация: фантомные пинги и силуэты, шумы HUD, смена позиции при раскрытии.", "key_points": ["Ложные пинги/силуэты, фантомные метки на карте; хаки HUD с бликами и шумами.", "Перемещается после раскрытия, использует дым/перегрузку сети, действует из тыла.", "Телеметрия: % игроков, купившихся на пинги, время на обнаружение, эффективность отвлечения."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Сетевая поддержка противника, нацеленная на дезориентацию сквада. Создает фантомные цели, мешает\\nвосприятию и ломает координацию, пока штурмовая группа заходит с фланга. Работает из тыла и меняет позицию\\nпри первом же раскрытии.\\n", "mechanics_links": [], "assets": []}, {"id": "abilities", "title": "Способности", "body": "• Фантомные пинги: размещает 2–4 ложных пинга на карте/сенсорах, живут 6–10с; кд 14–18с.  \\n• Голограммы-силуэты: проецирует 1–2 фантомных силуэта с шумом движения на 4–7с; кд 16–22с.  \\n• HUD-глитч: краткие всплески бликов/шумов, без урона, сбивают прицеливание на 0.5–1с; кд 12–16с.  \\n• Перегрузка сети: временно увеличивает задержку пинга клиентов на 20–35% в зоне 20–30 м, 3–5с; кд 24–30с.\\n", "mechanics_links": [], "assets": []}, {"id": "behavior", "title": "Поведение", "body": "• Держится за спинами боевой группы, перемещается после каждого раскрытия фантома.  \\n• При обнаружении — использует дым/ EMP-гранату и отходит на новую точку.  \\n• Работает от таймингов: пинги перед заходом союзников, HUD-глитч во время перестрелки, перегрузка для срыва пуша.\\n", "mechanics_links": [], "assets": []}, {"id": "tactics", "title": "Тактика и синергии", "body": "• Синергия с штурмовиками/снайперами: отвлекает, пока они заходят на позицию.  \\n• С турелями: фантомные метки заставляют игроков открываться под огонь.  \\n• Может bait''ить на мины/ловушки, подсвечивая ложные цели в опасных зонах.\\n", "mechanics_links": [], "assets": []}, {"id": "counters", "title": "Контр-игра", "body": "• Тепловизоры/ИК и сканеры различают фантомов; быстрая проверка пингов через короткие пики.  \\n• Давление на тыл, поиск источника сигнала; подавление сети, чтобы сорвать перегрузку.  \\n• Координация: callouts на подозрительные пинги, игнорирование одиночных «криков» без визуала.\\n", "mechanics_links": [], "assets": []}, {"id": "telemetry", "title": "Телеметрия", "body": "• fake_ping_belief_rate (% игроков, следовавших за ложным пингом).  \\n• time_to_detect_hacker, hologram_trigger_rate, hud_glitch_effective_time.  \\n• flank_success_rate_while_hacking, disengage_success_rate.  \\n• Цели: belief_rate 25–45% на неподготовленных, <20% на опытных; обнаружение ≤15с после первого фантома.\\n", "mechanics_links": [], "assets": []}, {"id": "loot", "title": "Лут", "body": "• Хакерские модули, импульсные гранаты, редкий софт для маскировки.  \\n• Данные о сетевой активности (низкая ценность), иногда — квестовый ключ для киберпространства.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1838, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-12-12", "author": "content_writer", "changes": "Создан профиль хакера-иллюзиониста с фантомными сигналами, голограммами и телеметрией."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

