INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-common-enemy-sniper-drone-mimic-1829', 'Снайпер-дрон «мимик»', '{"metadata": {"id": "npc-common-enemy-sniper-drone-mimic-1829", "title": "Снайпер-дрон «мимик»", "document_type": "canon", "category": "narrative", "status": "draft", "version": "1.0.0", "last_updated": "2025-12-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "enemy", "drone", "sniper", "stealth"], "topics": ["tactical", "ranged"], "related_systems": ["narrative-service", "gameplay-service", "ai-service", "analytics-service"], "related_documents": [{"id": "mechanics-combat-ai-enemies", "relation": "references"}, {"id": "mechanics-combat-combat-shooting-advanced", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/enemies/sniper-drone-mimic.md", "visibility": "internal", "audience": ["concept", "narrative", "gameplay"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужен профиль снайперского дрона с маскировкой, зарядным выстрелом и сменой позиции.", "goal": "Зафиксировать параметры камуфляжа, время зарядки/урон, смену позиции и телеметрию.", "essence": "Маскирующийся дрон: заряжает выстрел с предупреждением, раскрывается сканами тепла/шума, меняет позицию после выстрела.", "key_points": ["Камуфляж скрывает до скана; уязвим к термо/шум-детекту.", "Зарядный выстрел с предупреждением, высокий урон, низкий HP.", "Смена позиции после каждого выстрела.", "Телеметрия: хедшоты, обнаружения, убийства игроков."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Снайперский дрон с активной маскировкой под окружение. Работает на дистанции, даёт предупредительный луч,\\nзаряжает выстрел и сразу меняет позицию. Уязвим к сканам и ближнему бою.\\n", "mechanics_links": [], "assets": []}, {"id": "abilities", "title": "Способности", "body": "• Камуфляж: невиден до термо/шум-скана; раскрытие на 3–4с после выстрела; кд маски 10–16с.  \\n• Зарядный выстрел: предупреждение лазером 0.8–1.2с, высокий урон; кд 10–14с.  \\n• Смена позиции: обязательный уход на 12–20 м после выстрела; если в кайте — уходит в маску.  \\n• Ближний режим: слабые короткие очереди при сближении противника.\\n", "mechanics_links": [], "assets": []}, {"id": "behavior", "title": "Поведение", "body": "• Держит дистанцию, фокус по хил/нетраннерам.  \\n• При скане — отходит и перестраивает позицию.  \\n• Работает с турелью-кимера/киберпсом, используя их метки.\\n", "mechanics_links": [], "assets": []}, {"id": "counters", "title": "Контр-игра", "body": "• Термо/шум-скан, быстрый пуш в ближку.  \\n• Дым/помехи на лазер, чтобы сбить заряд.  \\n• EMP/взлом, чтобы вывести камуфляж из строя.\\n", "mechanics_links": [], "assets": []}, {"id": "telemetry", "title": "Телеметрия", "body": "• headshot_rate, detection_events, player_kills, reposition_time.  \\n• cloak_uptime, scan_reveal_rate, charge_cancel_events.  \\n• Цели: headshot_rate 20–30% (Tactical), reposition ≤5с после выстрела, scan_reveal_rate растёт в прогрессе игроков.\\n", "mechanics_links": [], "assets": []}, {"id": "loot", "title": "Лут", "body": "• Оптика, маск-модули, патроны высокоточки; редкий дроп на камуфляжный узел.  \\n• Данные о маршрутах патруля (низкая ценность).\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1829, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-12-12", "author": "content_writer", "changes": "Создан профиль снайпер-дрона «мимик» с камуфляжем, зарядным выстрелом и телеметрией."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO