INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-common-service-nomad-mechanic-2060s', 'NPC: Механик-номад — Badlands, 2060-е', '{"metadata": {"id": "npc-common-service-nomad-mechanic-2060s", "title": "NPC: Механик-номад — Badlands, 2060-е", "document_type": "canon", "category": "npc-lore/common", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-06T08:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "nomads", "badlands"], "topics": ["field-repair", "scavenging"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/service/nomad-mechanic-2060s-badlands.md", "visibility": "internal", "audience": ["concept", "narrative", "economy"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Полевой механик номадов был описан в Markdown и не синхронизировался с базой знаний.", "goal": "Структурировать профиль механика-номада для интеграции с событиями Badlands и сервисами обслуживания транспорта.", "essence": "Скиталец-механик ремонтирует технику в пустошах, поддерживает кланы и торгует деталями на выезде.", "key_points": ["Мобильная мастерская обеспечивает ремонт и модификации транспорта в Badlands.", "Персонаж использует подручные материалы и делится знаниями о выживании.", "Лояльность к номадским кланам выражается честными ценами для своих."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Механик постоянно перемещается между стоянками номадов, оснащая грузовик под мобильную мастерскую. Он знает\\nспецифику пустынных трасс, умеет восстанавливать технику после песчаных бурь и подбирает детали для редких моделей.\\n", "mechanics_links": [], "assets": []}, {"id": "services", "title": "Услуги", "body": "Базовый набор включает ремонт ходовой части, установку усиленных фильтров и модернизацию подвески. Дополнительно\\nмеханик продаёт запасные части и консультирует по маршрутам и защите от рейдеров.\\n", "mechanics_links": [], "assets": []}, {"id": "traits", "title": "Особенности и навыки", "body": "Работает прямо в пустоши, использует импровизированные инструменты и знает секреты номадов по переработке лома.\\nЛоялен к своим кланам, но осторожен с чужаками и требует оплату вперёд.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Конвертирован профиль механика-номада в YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

