INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-common-service-street-doc-2065-heywood', 'Уличный доктор Heywood (2065)', '{"metadata": {"id": "npc-common-service-street-doc-2065-heywood", "title": "Уличный доктор Heywood (2065)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "street-doc", "heywood"], "topics": ["npc_profiles", "medical_underworld"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/service/street-doc-2065-heywood.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Требуется описать подпольного медика Heywood для использования в квестах и экономике медицинских услуг.", "goal": "Формализовать профиль NPC с услугами, особенностями и сетевыми связями.", "essence": "Независимый уличный доктор лечит пострадавших без вопросов, предлагая дешёвые, но рискованные операции.", "key_points": ["Работает подпольно и не сотрудничает с NCPD.", "Предоставляет базовые импланты и медикаменты по сниженным ценам.", "Имеет связи с местными бандами, влияя на безопасность района."]}, "content": {"sections": [{"id": "profile", "title": "Профиль NPC", "body": "Независимый уличный доктор, ведущий подпольную клинику в Heywood. Активен с 2065 года, обслуживает жителей района и представителей низших слоёв, использует базовое оборудование.\\n", "mechanics_links": [], "assets": []}, {"id": "services", "title": "Доступные услуги", "body": "- Лечение ранений и остановка кровотечения.\\n- Базовые хирургические процедуры и установка простых имплантов.\\n- Продажа медикаментов первой необходимости.\\n", "mechanics_links": ["mechanics/economy/economy-contracts.yaml"], "assets": []}, {"id": "traits", "title": "Особенности и связи", "body": "Доктор не задаёт вопросов клиентам, держит цены ниже риппердоков и опирается на поддержку местных банд. Оборудование клиники устаревшее, качество услуг среднее.\\n", "mechanics_links": [], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "NPC может выступать точкой входа в миссии по спасению раненых, поставке медикаментов, а также в сюжетах про нелегальные импланты или давление со стороны корпораций.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертировано описание уличного доктора Heywood в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

