INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-common-enemy-tank-synth-harpoon-1827', 'Танковый синт с гарпуном', '{"metadata": {"id": "npc-common-enemy-tank-synth-harpoon-1827", "title": "Танковый синт с гарпуном", "document_type": "canon", "category": "narrative", "status": "draft", "version": "1.0.0", "last_updated": "2025-12-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "enemy", "tank", "harpoon"], "topics": ["frontline", "control"], "related_systems": ["narrative-service", "gameplay-service", "ai-service", "analytics-service"], "related_documents": [{"id": "mechanics-combat-ai-enemies", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/enemies/tank-synth-harpoon.md", "visibility": "internal", "audience": ["concept", "narrative", "gameplay"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужен профиль тяжёлого синта с гарпуном и анкером для удержания позиции.", "goal": "Зафиксировать параметры гарпуна, анкер-баффы, урон конуса и телеметрию.", "essence": "Фронтовой танк: притягивает гарпуном, анкерится для снижения отдачи, но уязвим к взлому якоря.", "key_points": ["Гарпун-пулл, анкер в землю, конусный шотган и АоЕ удар.", "Уязвим к взлому якоря и флангу.", "Телеметрия: успешные притяжения, смерти притянутых."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Тяжёлый синт, удерживает choke, притягивает цели и анкерится для стабильной стрельбы. Опасен спереди,\\nно уязвим к флангу и взлому якоря.\\n", "mechanics_links": [], "assets": []}, {"id": "abilities", "title": "Способности", "body": "• Гарпун: дистанция 12–18 м, кд 10–14с, притягивает и кратко замедляет цель.  \\n• Анкер: закрепляется, получая устойчивость (-40% отдача, -20% входящий урон), но замедляется; взлом якоря снимает бафф.  \\n• Шотган-конус: средний урон, эффективен вблизи.  \\n• АоЕ удар: удар по площади 3–4 м, кд 12–16с.\\n", "mechanics_links": [], "assets": []}, {"id": "behavior", "title": "Поведение", "body": "• Анкерится при встрече сквада, держит choke.  \\n• Если потерял HP — притягивает хилера/нетраннера для разрыва построения.  \\n• При взломе якоря вынужден двигаться и теряет гарпун до восстановления.\\n", "mechanics_links": [], "assets": []}, {"id": "counters", "title": "Контр-игра", "body": "• Взлом якоря, чтобы отключить гарпун и бафф.  \\n• Фланг/высота, чтобы обойти конус и АоЕ.  \\n• Контроль после притяжения, чтобы не дать конвертировать килл.\\n", "mechanics_links": [], "assets": []}, {"id": "telemetry", "title": "Телеметрия", "body": "• harpoon_success, pulled_target_deaths, anchor_uptime.  \\n• cone_kills, aoe_hits, anchor_hack_events.  \\n• Цели: harpoon_success 35–55%, pulled_deaths умеренные, anchor_hack как контрмера.\\n", "mechanics_links": [], "assets": []}, {"id": "loot", "title": "Лут", "body": "• Гарпун-мод, якорные болты, тяжёлые пластины; редкий чертёж шотган-мода.  \\n• Данные о маршрутах (низкая ценность).\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1827, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-12-12", "author": "content_writer", "changes": "Создан профиль танкового синта с гарпуном, анкером и телеметрией."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

