INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('common-trader-cyberware-2070s-westbrook-001', 'Подпольный кибервар (Westbrook, 2070-е)', '{"metadata": {"id": "common-trader-cyberware-2070s-westbrook-001", "title": "Подпольный кибервар (Westbrook, 2070-е)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "trader", "cyberware", "westbrook"], "topics": ["black_market", "cyberware_supply"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [{"id": "mechanics/combat/combat-implants.yaml", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/traders/cyberware-blackmarket-2070s-westbrook.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Подпольный продавец кибервара существовал в Markdown и не был интегрирован в систему чёрного рынка.", "goal": "Формализовать профиль дилера нелегальных имплантов для Westbrook.", "essence": "Торговец предлагает редкие и опасные импланты, продаёт их по репутации и проверкам безопасности.", "key_points": ["Реализует редкие импланты с повышенным риском.", "Требует репутацию и рекомендации для покупки.", "Служит источником сюжетов о контрабанде и NetWatch."]}, "content": {"sections": [{"id": "inventory", "title": "Ассортимент", "body": "Доступны экспериментальные нейрошины, нелегальные Sandevistan-модули и биоответчики. Товары перемещаются через\\nподпольные каналы Westbrook и меняются ежедневно.\\n", "mechanics_links": ["mechanics/combat/combat-implants.yaml"], "assets": []}, {"id": "access", "title": "Условия доступа", "body": "Покупатели проходят проверку репутации и должны предоставить рекомендации фиксёра. Дилер отказывает игрокам с\\nнизким рейтингом и отмечает подозрительных клиентов для NetWatch.\\n", "mechanics_links": ["mechanics/social/player-orders-via-npc.yaml"], "assets": []}, {"id": "dialogue", "title": "Речь и стиль", "body": "Прямолинейная манера: «Смотри, но не трогай — пока не заплатишь.» Подчёркивает риск сделки и напоминает о\\nпоследствиях несанкционированной установки.\\n", "mechanics_links": [], "assets": []}, {"id": "hooks", "title": "Наративные хуки", "body": "- Квесты по добыче нелегального кибервара.\\n- Сюжеты о рейдах NetWatch и скрытой транспортировке.\\n- Обновление ассортимента после успешных миссий игрока.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль подпольного кибервара Westbrook в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

