INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-jake-archer', 'NPC: Джейк Арчер', '{"metadata": {"id": "npc-jake-archer", "title": "NPC: Джейк Арчер", "document_type": "canon", "category": "npc-lore/common", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-05T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "downtown", "trader"], "topics": ["economy", "street-trade"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [{"id": "npc-status-tracker", "relation": "references"}, {"id": "npc-dialogues-basic", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/traders/jake-archer.md", "visibility": "internal", "audience": ["concept", "narrative", "economy"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль независимого торговца Downtown существовал только в Markdown и не был закреплён в каноне.", "goal": "Перенести сведения о Джейке Арчере в единый формат знаний для синхронизации с экономическими и нарративными системами.", "essence": "Бывший корпо, перешедший в независимую торговлю, продаёт качественные товары и реагирует на репутацию игрока.", "key_points": ["Предлагает базовую и среднюю экипировку, медикаменты и инструменты.", "Репутация и объём покупок влияют на скидки и ассортимент.", "Служит контактной точкой для квестов по доставке и поиску товаров."]}, "content": {"sections": [{"id": "background", "title": "Происхождение и контекст", "body": "Джейк начинал карьеру в корпорации, но ушёл в Downtown ради независимости и гибкости. Магазин располагается в\\nоживлённом торговом квартале, где он взаимодействует с корпорациями, бандами и фрилансерами, сохраняя нейтралитет.\\n", "mechanics_links": [], "assets": []}, {"id": "appearance", "title": "Внешность и модификации", "body": "Сдержанный деловой стиль, тёмные костюмы и минимальные импланты связи и анализа данных. Подчёркивает образ\\nпрофессионального продавца, способного работать с корпоративными клиентами.\\n", "mechanics_links": [], "assets": []}, {"id": "personality", "title": "Личность и мотивация", "body": "Прагматичный и дружелюбный торговец, стремящийся расширять ассортимент и удерживать клиентов. Боитcя потерять\\nбизнес и уделяет внимание качеству товара и отзывам покупателей.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровая роль", "body": "Предлагает оружие, броню, медикаменты и инструменты среднего уровня. Выступает точкой входа в квесты «Торговля»,\\n«Доставка» и «Поиск товаров». При росте доверия открывает эксклюзивные позиции и скидки.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и взаимодействие", "body": "Поддерживает нейтралитет с основными корпорациями и бандами, работая с теми, кто платит. Игроки повышают доверие\\nрегулярными покупками и корректной репутацией. Другие торговцы рассматривают его как надёжного партнёра.\\n", "mechanics_links": [], "assets": []}, {"id": "dialogue", "title": "Речь и паттерны поведения", "body": "Использует непринуждённые коммерческие фразы, подчёркивающие гарантии качества. Интонация меняется от дружелюбной\\nдо сдержанной в зависимости от репутации игрока и успешности сделок.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-05", "author": "narrative_team", "changes": "Конвертирован профиль Джейка Арчера в структурированный формат."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

