INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-common-enemy-wraith-2050s', 'Налётчик Wraiths (2050-е, Badlands)', '{"metadata": {"id": "npc-common-enemy-wraith-2050s", "title": "Налётчик Wraiths (2050-е, Badlands)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "wraiths", "badlands"], "topics": ["nomad_conflict", "enemy_profiles"], "related_systems": ["combat-service", "economy-service"], "related_documents": [{"id": "mechanics-combat-combat-pvp-pve", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/enemies/wraith-raider-2050s-badlands.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Требуется структурировать профиль налётчиков Wraiths для сценариев в Badlands.", "goal": "Обеспечить единый источник данных о тактике, угрозе и наградах врагов Wraiths.", "essence": "Налётчики Wraiths — агрессивные номады, атакующие караваны и путников в пустошах ради трофеев.", "key_points": ["Используют быстрые рейды на транспорте и работают группами.", "Представляют средний уровень угрозы с упором на мобильность.", "Приносят лут, влияющий на экономику запчастей и оружия."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Типичный налётчик банды Wraiths действует в Badlands, нападая на караваны, кочевые поселения и одиноких путников. Группы 3–5 бойцов используют модернизированные автомобили и импровизированное вооружение.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "combat", "title": "Боевые характеристики", "body": "- Уровень угрозы: средний.\\n- Вооружение: лёгкое и среднее оружие, взрывчатка, ловушки.\\n- Тактика: быстрые засады, атаки с транспорта, отступление после захвата трофеев.\\n- Группа: координируемые рейды с разведкой и поддержкой.\\n", "mechanics_links": ["mechanics/combat/combat-pvp-pve.yaml"], "assets": []}, {"id": "loot", "title": "Лут и последствия", "body": "- Оружие среднего качества.\\n- Боеприпасы и запчасти для транспорта.\\n- Награбленные товары, влияющие на квесты и торговлю.\\nПобеда над Wraiths может укрепить отношения с номадами и открыть контракты на охрану караванов.\\n", "mechanics_links": ["mechanics/economy/economy-logistics.yaml"], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- Миссии по сопровождению караванов через Badlands.\\n- Контр-рейды против баз Wraiths.\\n- Сюжеты о похищениях и спасении пленных.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}]}, "appendix": {"glossary": [{"term": "Wraiths", "definition": "Номадская банда-мародёр, доминирующая в пустошах Badlands."}], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль налётчика Wraiths в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

