INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-common-police-max-tac-2077-cc', 'Оперативник MAX-TAC (2077, City Center)', '{"metadata": {"id": "npc-common-police-max-tac-2077-cc", "title": "Оперативник MAX-TAC (2077, City Center)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "max-tac", "ncpd"], "topics": ["police_operations", "cyberpsychosis_response"], "related_systems": ["combat-service", "world-simulation"], "related_documents": [{"id": "mechanics-combat-combat-hacking-combat-integration", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/police/max-tac-operative-2077-city-center.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Необходимо описать профиль оперативников MAX-TAC, реагирующих на киберпсихоз в City Center.", "goal": "Стандартизировать данные NPC для сценариев высокой угрозы и интеграции в события NCPD.", "essence": "MAX-TAC — элитный отряд NCPD, прибывающий при максимальном уровне розыска и нейтрализующий киберпсихов без компромиссов.", "key_points": ["Экстремально модифицированные бойцы с военным опытом.", "Используют скоординированные тактики подавления в составе групп.", "Вмешиваются только при критической угрозе (4–5 звёзд розыска)."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Элитный оперативник MAX-TAC (Psycho Squad) Найт-Сити. Подразделение специализируется на нейтрализации киберпсихов и особо опасных преступников. Работает из City Center, выезжая по вызовам по всему городу.\\n", "mechanics_links": ["mechanics/combat/combat-implants.yaml"], "assets": []}, {"id": "combat", "title": "Боевые характеристики", "body": "- Уровень угрозы: очень высокий.\\n- Экипировка: военный класс вооружения и брони.\\n- Импланты: экстремальные боевые модификации.\\n- Группа: 4–6 оперативников с чёткой ролевой специализацией.\\n- Тактика: подавление и нейтрализация с использованием энергетического и баллистического оружия.\\n", "mechanics_links": ["mechanics/combat/combat-pvp-pve.yaml"], "assets": []}, {"id": "behaviour", "title": "Поведение и особенности", "body": "MAX-TAC выезжает только при 4–5 звёздах розыска или подтверждённом киберпсихозе. Оперативники не берут взяток, действуют без предупреждений и ориентированы на ликвидацию угрозы, а не на арест.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- Игроки могут наблюдать прибытие отряда в событиях с киберпсихозом.\\n- Возможны миссии по взаимодействию или противостоянию MAX-TAC.\\n- Отражает эскалацию городской опасности и реакцию NCPD.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}]}, "appendix": {"glossary": [{"term": "MAX-TAC", "definition": "Специализированное подразделение NCPD по борьбе с киберпсихами."}], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль оперативника MAX-TAC в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

