INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-common-police-patrol-2070s-watson', 'NPC: Патрульный NCPD — Watson, 2070-е', '{"metadata": {"id": "npc-common-police-patrol-2070s-watson", "title": "NPC: Патрульный NCPD — Watson, 2070-е", "document_type": "canon", "category": "npc-lore/common", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-06T08:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "ncpd", "watson"], "topics": ["street-security", "law-enforcement"], "related_systems": ["narrative-service", "police-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/police/ncpd-patrol-officer-2070s-watson.md", "visibility": "internal", "audience": ["concept", "narrative", "systems"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Патрульный NCPD был описан в Markdown и не был доступен в канонической базе данных.", "goal": "Перенести сведения о патрульном офицере Watson в структурированный формат для систем охраны города.", "essence": "Базовый полицейский реагирует на преступления, патрулирует Watson и может взаимодействовать с игроком через взятки или эскалацию угрозы.", "key_points": ["Поддерживает повседневную охрану Watson и реагирует на нарушения.", "Обладает средним уровнем боевой угрозы и стандартным вооружением.", "Может быть подкуплен и вызывает подкрепление при эскалации."]}, "content": {"sections": [{"id": "duties", "title": "Обязанности", "body": "Патрульный следит за порядком на улицах Watson, проверяет подозрительные активности и фиксирует нарушения.\\nПри угрозе жизни вызывает поддержку и координирует перекрытие кварталов.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровая роль", "body": "В игре офицер появляется при развитии уровня розыска, может быть подкуплен для снижения угрозы или передачи информации.\\nИгроки сталкиваются с ним в уличных событиях, дорожных блокпостах и при расследовании правонарушений.\\n", "mechanics_links": [], "assets": []}, {"id": "combat_profile", "title": "Боевой профиль", "body": "Использует стандартные пистолеты и карабины, носит защитные жилеты и действует в паре или малой группе.\\nПри длительной перестрелке инициирует запрос подкрепления и может применить нелетальные средства.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Конвертирован профиль патрульного NCPD в YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

