INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('canon-narrative-npc-field-medic', 'AI враг: Медик-полевик (реаниматор)', '{"metadata": {"id": "canon-narrative-npc-field-medic", "title": "AI враг: Медик-полевик (реаниматор)", "document_type": "canon", "category": "narrative", "status": "approved", "version": "1.0.0", "last_updated": "2025-12-13T12:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-12-08T21:56:43+00:00", "owners": [{"role": "content_writer", "contact": "content@necp.game"}], "tags": ["npc", "ai-enemy", "combat", "medic", "support"], "topics": ["narrative", "gameplay", "combat-systems"], "related_systems": ["combat-service", "ai-enemy-service", "telemetry-service"], "related_documents": [{"id": "canon-mechanics-combat-enemies", "relation": "implements"}, {"id": "canon-narrative-npc-common-template", "relation": "based_on"}], "source": "knowledge/canon/narrative/npc-lore/common/ai-enemy-field-medic-2020-2093.yaml", "visibility": "internal", "audience": ["content", "combat-design", "ai-developers"], "risk_level": "low"}, "review": {"chain": [{"role": "content_writer", "reviewer": "Content Writer Agent", "reviewed_at": "2025-12-13T12:00:00+00:00", "status": "approved"}], "next_actions": []}, "summary": {"problem": "Необходим AI враг поддержки для тактических боёв", "goal": "Создать сбалансированного медика, который лечит союзников и создаёт тактические окна", "essence": "Боевой медик с реанимацией, дымом/щитом и ограниченными зарядами", "key_points": ["Реанимация с каналом и защитой поднимаемого", "Лечение на расстоянии с ограниченными зарядами", "Синергия с лидерами и щитовиками", "Тактическое поведение с укрытиями"]}, "content": {"identity": {"name": "Field Medic", "aliases": ["Combat Medic", "Support Unit", "Reanimator"], "unique_id": "ai-enemy-field-medic-001", "category": "ai-enemy", "type": "support", "era": "2020-2093", "active_years": "2020-2093", "threat_level": "tactical", "facti