INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-common-police-detective-2070s-center-001', 'Детектив NCPD (City Center, 2070-е)', '{"metadata": {"id": "npc-common-police-detective-2070s-center-001", "title": "Детектив NCPD (City Center, 2070-е)", "document_type": "canon", "category": "narrative", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "ncpd", "city-center"], "topics": ["policing", "night-city-law"], "related_systems": ["narrative-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/police/ncpd-detective-2070s-city-center.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужно описать базового представителя полиции City Center 2070-х годов для сюжетных сцен.", "goal": "Зафиксировать роль детектива NCPD, ключевую реплику и временной контекст службы.", "essence": "Опытный полицейский, курирующий расследования и взаимодействие с корпорациями в центре города.", "key_points": ["Активен в 2072–2079 годах, работает в City Center.", "Основной фокус — расследования с участием корпораций.", "Сохраняет профессиональную дистанцию и скепсис к совпадениям."]}, "content": {"sections": [{"id": "overview", "title": "Краткий профиль", "body": "Детектив полиции Night City, закреплённый за центральным районом. Занимается расследованиями, где пересекаются интересы корпораций и городской инфраструктуры. Авторитет среди коллег строится на умении находить юридически безупречные доказательства и выдерживать давление корпордепартаментов безопасности.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay-hooks", "title": "Геймплейные акценты", "body": "• Может выступать источником квестов, связанных с расследованиями и корпоративными конфликтами.\\n• Помогает игроку легализовать найденные доказательства при условии соблюдения процедур.\\n• При попытке обойти его правила обращается к внутренней безопасности.\\n", "mechanics_links": [], "assets": []}, {"id": "dialogue", "title": "Пример реплики", "body": "\\"Докажи, что это не совпадение, и я двинусь дальше.\\"\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_director", "changes": "Конвертация профиля детектива NCPD из MD в YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

