INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('common-service-bouncer-2070s-japantown-001', 'Вышибала (Japantown, 2070-е)', '{"metadata": {"id": "common-service-bouncer-2070s-japantown-001", "title": "Вышибала (Japantown, 2070-е)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "japantown", "security"], "topics": ["nightlife", "access_control"], "related_systems": ["narrative-service", "security-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/service/bouncer-2070s-japantown.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль вышибалы Japantown находился в Markdown и не использовался в сценариях контроля доступа.", "goal": "Описать роль уличной охраны для интеграции с ночной жизнью и системами репутации району Japantown.", "essence": "Вышибала фильтрует гостей клубов, следит за безопасностью и запоминает постоянных клиентов.", "key_points": ["Контролирует вход по спискам и репутации.", "Быстро реагирует на угрозы внутри и снаружи клуба.", "Может предоставить игроку доступ к эксклюзивным событиям при достаточном доверии."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Работает на линии клубов Japantown с 2070 по 2078 год. Оценивает посетителей по внешнему виду, поведению и\\nрекомендациям фиксёров. Поддерживает связь с внутренней охраной и системой камер.\\n", "mechanics_links": ["mechanics/social/relationships-system.yaml"], "assets": []}, {"id": "duties", "title": "Обязанности", "body": "- Проверка списков и идентификаторов гостей.\\n- Мониторинг улицы и фильтрация проблемных посетителей.\\n- Вызов усиления при конфликте.\\n- Сигналы бармену и менеджеру о важных гостях.\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "dialogue", "title": "Речевой стиль", "body": "Использует короткие фразы, подчёркивающие правила клуба. Классическая реплика: «Правила на двери — не для мебели.»\\nЛояльным игрокам добавляет информацию о грядущих вечеринках или скрытых комнатах.\\n", "mechanics_links": [], "assets": []}, {"id": "hooks", "title": "Наративные хуки", "body": "- Сюжеты по проникновению в клуб через убеждение или взятку.\\n- События с улаживанием драки у входа.\\n- Миссии по защите VIP-гостя или проверке поддельных пропусков.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль вышибалы Japantown в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

