INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('common-service-mechanic-2060s-santo-001', 'Механик (Santo Domingo, 2060-е)', '{"metadata": {"id": "common-service-mechanic-2060s-santo-001", "title": "Механик (Santo Domingo, 2060-е)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "santo-domingo", "mechanic"], "topics": ["vehicle_maintenance", "nomad_support"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [{"id": "mechanics/economy/economy-logistics", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/service/mechanic-2060s-santo-domingo.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Полевой механик Santo Domingo описывался в Markdown и не участвовал в системе сервисных NPC.", "goal": "Зафиксировать профиль механика для взаимодействий с кочевниками и ремонтом транспорта игроков.", "essence": "Механик из Santo Domingo специализируется на апгрейдах и восстановлении техники в городских доках.", "key_points": ["Работает с кочевниками и городскими командами.", "Обеспечивает апгрейды ходовой, брони и энергетики транспорта.", "Задаёт тон локальной экономике автосервисов."]}, "content": {"sections": [{"id": "workshop", "title": "Мастерская", "body": "Мастерская расположена в промзоне Santo Domingo и оснащена подъёмниками, печами для напыления и сканерами\\nшасси. Механик обслуживает технику 2061–2069 годов, ведёт журнал поправок для кланов и городских курьеров.\\n", "mechanics_links": ["mechanics/economy/equipment/procedural-equipment-library.yaml"], "assets": []}, {"id": "services", "title": "Услуги", "body": "- Ремонт ходовой части и бронепанелей.\\n- Установка усиленных двигателей и систем охлаждения.\\n- Настройка импульсных гашеток и энергоячеек для электротранспорта.\\n- Срочный ремонт после стычек с рейдерами.\\n", "mechanics_links": ["mechanics/economy/economy-contracts.yaml"], "assets": []}, {"id": "dialogue", "title": "Речевые сигнатуры", "body": "Разговаривает прямолинейно, уважает игроков, которые платят вовремя. Любимая фраза: «Гайка к гайке — и снова зверь.»\\nПредлагает апгрейды, если видит потенциал в машине игрока.\\n", "mechanics_links": [], "assets": []}, {"id": "hooks", "title": "Наративные хуки", "body": "- Контракты на восстановление транспорта после рейдов Wraiths.\\n- Квесты по поиску редких запчастей.\\n- Улучшения для гоночных событий Santo Domingo.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль механика Santo Domingo в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

