INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-arasaka-director-counter-intel-2030-2045', 'NPC: Акира Танака — директор Counter-Intel Arasaka (2030–2045)', '{"metadata": {"id": "npc-arasaka-director-counter-intel-2030-2045", "title": "NPC: Акира Танака — директор Counter-Intel Arasaka (2030–2045)", "document_type": "canon", "category": "npc-lore/corporate", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "arasaka", "counter-intel"], "topics": ["espionage", "corporate-security"], "related_systems": ["narrative-service", "quest-service"], "related_documents": [{"id": "arasaka-2020-2093", "relation": "references"}, {"id": "factions-overview", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/corporations/arasaka/director-counter-intel-2030-2045.md", "visibility": "internal", "audience": ["concept", "narrative", "quest"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Подробный Markdown-профиль Акиры Танаки нуждался в переносе в структуру знаний для синхронизации с контр-шпионскими арками.", "goal": "Сформировать каноническую запись о директоре Counter-Intel Arasaka и его роли в купольных войнах.", "essence": "Холодный аналитик управляет защитой корпоративных секретов, вербует агентов и формирует квесты контр-разведки.", "key_points": ["Возглавляет Counter-Intel Arasaka с 2030 по 2045 годы, обеспечивая защиту проекта Relic и купольных операций.", "Обладает высокоуровневой кибернетикой для скрытности, контр-взлома и аналитики.", "Предоставляет игрокам ветки вербовки, защиты данных и разоблачения шпионов."]}, "content": {"sections": [{"id": "profile", "title": "Профиль и контекст", "body": "Акира Танака, прозвище «Тень», вырос внутри корпоративной семьи Arasaka и превратился в директора Counter-Intel.\\nЕго сфера ответственности — защита проектов корпорации, выявление шпионов и управление скрытыми операциями во время\\nкупольных войн.\\n", "mechanics_links": ["shared/docs/knowledge/mechanics/world/world-governance.yaml"], "assets": []}, {"id": "appearance", "title": "Внешность и модификации", "body": "Атлетичный мужчина 40–45 лет с аккуратной короткой стрижкой, минимальными визуальными имплантами и строгим\\nтёмным стилем. Использует военный Neural Link, профессиональные Kiroshi, Subdermal Armor, рефлекторные усилители и\\nвысокоуровневый кибердек.\\n", "mechanics_links": ["shared/docs/knowledge/mechanics/combat/combat-implants-system.yaml"], "assets": []}, {"id": "history", "title": "Биография и ключевые операции", "body": "Вырос в Токио, прошёл специализированную подготовку контр-разведчика. Ключевые операции: раскрытие шпионской сети\\nMilitech (2032), защита проекта Relic (2035), нейтрализация саботажа серверов (2038) и вербовка двойного агента\\n(2040). Управляет департаментом в период купольных войн и купольных конфликтов в Найт-Сити.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и цели", "body": "Стремится к абсолютному контролю над информацией и защите корпорации от внешних и внутренних угроз. Боится крупных\\nутечек, предательства и успешных взломов. Его желания — безупречная защита данных, лояльность персонала и контроль\\nнад информационными потоками.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и взаимодействие", "body": "Лоялен Arasaka, враждебен к Militech, подозрителен к большинству фракций. Сотрудничает с NCPD и NetWatch по\\nконтрактам, но остаётся настороженным. Отношение к игроку зависит от доказанной лояльности и успеха операций.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровая роль", "body": "Предлагает квесты по поиску шпионов, защите проектов, вербовке агентов и расследованию утечек. Ветвления охватывают\\nоперации «Контракт Watson», «Секрет Relic» и «Двойной агент», открывая доступ к уникальным контр-разведывательным\\nуслугам.\\n", "mechanics_links": ["shared/docs/knowledge/mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "dialogue", "title": "Манера речи", "body": "Речь формальная и лаконичная. Часто повторяет принципы параноидальной осторожности: «Доверие — это слабость»,\\n«Информация — оружие». Приветствия адаптируются к репутации — от подозрительных до уважительных.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"type": "internal", "link": "shared/docs/knowledge/canon/narrative/npc-lore/factions/corporations/arasaka-2020-2093.md"}, {"type": "internal", "link": "shared/docs/knowledge/canon/lore/_03-lore/factions/factions-overview.md"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован профиль Акиры Танаки в структурированное знание."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

