INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('common-service-medtech-2040s-watson-001', 'Медтех (Watson, 2040-е)', '{"metadata": {"id": "common-service-medtech-2040s-watson-001", "title": "Медтех (Watson, 2040-е)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "watson", "medtech"], "topics": ["street_medicine", "ripperdoc_networks"], "related_systems": ["narrative-service", "health-system"], "related_documents": [{"id": "mechanics/social/mentorship-relationships", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/service/medtech-nurse-2040s-watson.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль медтеха Watson не был структурирован и не использовался в сюжетах уличной медицины.", "goal": "Описать медсестру, соединяющую пациентов и риппердоков в Watson, для последующего использования в квестах и событиях.", "essence": "Медтех предоставляет первичную помощь, организует связь с риппердоками и помогает жителям во время кризисов.", "key_points": ["Специалист по уличной медицине с доступом к сетям риппердоков.", "Поддерживает простые процедуры и стабилизацию пациентов.", "Способствует сюжетам поддержки комьюнити в Watson."]}, "content": {"sections": [{"id": "clinic", "title": "Локация и обязанности", "body": "Работает в маленькой клинике Watson в 2044–2049 годах. Смена длится 14 часов: медтех стабилизирует пациентов,\\nоформляет переводы к риппердокам и держит запас медицинских имплантов средней категории.\\n", "mechanics_links": ["mechanics/social/mentorship-system.yaml"], "assets": []}, {"id": "services", "title": "Услуги", "body": "- Первая помощь и стабилизация после уличных перестрелок.\\n- Организация очереди к риппердокам и объяснение рисков имплантации.\\n- Доступ к дешёвым медикаментам и разрешённым стимуляторам.\\n- Помощь в страховках Trauma Team для жителей Watson.\\n", "mechanics_links": ["mechanics/economy/economy-contracts.yaml"], "assets": []}, {"id": "dialogue", "title": "Речевые паттерны", "body": "Спокойная и поддерживающая манера общения. Ключевая фраза: «Дышите ровно — сейчас станет легче.»\\nПри низкой репутации персонажа напомнит о правилах клиники и стоимости услуг.\\n", "mechanics_links": [], "assets": []}, {"id": "hooks", "title": "Наративные хуки", "body": "- Квесты на поиск редких медикаментов.\\n- События по эвакуации пациентов во время кризиса.\\n- Линии доверия для получения скидок на медуслуги и импланты.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль медтеха Watson в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

