INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-arasaka-vp-ops-2078-2090', 'VP Operations Arasaka (2078-2090)', '{"metadata": {"id": "npc-arasaka-vp-ops-2078-2090", "title": "VP Operations Arasaka (2078-2090)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "arasaka", "executive"], "topics": ["global-operations", "crisis-management"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [{"id": "scenarios-factions-corporations-arasaka-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/corporations/arasaka/vp-operations-2078-2090.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль вице-президента по операциям Arasaka находился в Markdown и не использовался в сюжетах корпоративного контроля.", "goal": "Стандартизировать данные о Рэе Танака для квестов антикризисного управления и глобальных реорганизаций Arasaka.", "essence": "Антикризисный менеджер унифицирует процессы корпорации, подавляя саботаж и расширяя влияние Arasaka.", "key_points": ["Руководит операционными кластерами в Азии, Европе и Найт-Сити.", "Известен жёсткими реорганизациями и дисциплиной.", "Использует игроков для стабилизации филиалов и устранения угроз."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Рей Танака — вице-президент по операциям Arasaka, курирующий глобальные кластеры. Премиальный корпоративный стиль\\nи высококлассные импланты подчеркивают статус и требовательность к подчинённым.\\n", "mechanics_links": ["mechanics/economy/economy-logistics.yaml"], "assets": []}, {"id": "strategy", "title": "Стратегия и методы", "body": "Поднимается через операционные офисы Азии и Европы, вводит единые стандарты, ликвидирует убыточные проекты и\\nвнедряет автоматизированные системы отчётности. Известен бескомпромиссными решениями.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и цели", "body": "Одержим эффективностью и контролем. Цель — унификация процессов 2078-2090 и продвижение на пост COO/CEO.\\nОпасается саботажа филиалов и внешних атак конкурентов.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и конкуренты", "body": "Противостоит Militech за контракты, сотрудничает с NetWatch по протоколам безопасности и подавляет влияние\\nлокальных банд на цепочки поставок.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровая роль", "body": "Даёт задания по стабилизации филиалов, расследованию саботажа и внедрению корпоративных стандартов. Навыки:\\nсоциальные — высокие, технические — средние, боевые — низкие. Успех миссий открывает доступ к exec-перкам.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- Антикризисное управление филиалом в Найт-Сити.\\n- Внутренние интриги за должность COO.\\n- Подавление профсоюзных и бандитских влияний на производственные цепочки.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль VP Operations Arasaka в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

