INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('common-service-taxi-2070s-center-001', 'Таксист (City Center, 2070-е)', '{"metadata": {"id": "common-service-taxi-2070s-center-001", "title": "Таксист (City Center, 2070-е)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "city-center", "transport"], "topics": ["urban_navigation", "driver_contracts"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [{"id": "mechanics/world/world-state/player-impact-systems", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/service/taxi-driver-2070s-city-center.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль таксиста City Center был в Markdown и не использовался для событий транспортных сетей.", "goal": "Зафиксировать образ городского водителя, собирающего слухи и доставляющего игроков по опасным маршрутам.", "essence": "Таксист обеспечивает быстрые перевозки, делится информацией о районах и реагирует на угрозы в пути.", "key_points": ["Знает безопасные и рискованные маршруты City Center.", "Предлагает услуги ночных перевозок с бонусом слухов.", "Может становиться источником ситуативных квестов и эвакуаций."]}, "content": {"sections": [{"id": "operations", "title": "Рабочая смена", "body": "Работает в City Center с 2073 по 2079 годы. Использует бронированный седан с автопилотом и ручным управлением.\\nИмеет систему сканеров для отслеживания дорожных блоков и активных боёв.\\n", "mechanics_links": ["mechanics/economy/economy-logistics.yaml"], "assets": []}, {"id": "services", "title": "Услуги", "body": "- Транспортировка игроков между кварталами без случайных встреч.\\n- Маршруты с повышенным риском за дополнительную плату.\\n- Передача актуальных слухов о блокпостах, рейдах MAX-TAC и событиях фиксёров.\\n- Возможность срочной эвакуации из опасной зоны.\\n", "mechanics_links": ["mechanics/social/player-orders-via-npc.yaml"], "assets": []}, {"id": "dialogue", "title": "Речь и поведение", "body": "Общается уверенно, с лёгким юмором. Характерная фраза: «Скажи адрес — остальное на мне.».\\nДелится советами по навигации и предупреждает о горячих точках.\\n", "mechanics_links": [], "assets": []}, {"id": "hooks", "title": "Наративные хуки", "body": "- Квесты на сопровождение VIP через опасные районы.\\n- Сбор слухов для фиксёров.\\n- Мини-игра с выбором маршрута и управлением рисками.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль таксиста City Center в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

