INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('common-trader-bd-2070s-japantown-001', 'BD-дилер (Japantown, 2070-е)', '{"metadata": {"id": "common-trader-bd-2070s-japantown-001", "title": "BD-дилер (Japantown, 2070-е)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "trader", "japantown", "braindance"], "topics": ["bd_market", "nightlife"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [{"id": "mechanics/social/player-orders-creation", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/traders/bd-dealer-2070s-japantown.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль BD-дилера существовал в Markdown и не был синхронизирован с системами торговли и ночной жизни.", "goal": "Структурировать образ дилера брайндансов для сценариев Japantown и экономики нелегальных развлечений.", "essence": "Дилер продаёт легальные и серые BD-записи, собирает слухи и может выступать проводником в подпольные каталоги.", "key_points": ["Доступ к редким BDs и эксклюзивным впечатлениям.", "Взаимодействует с ночными клубами и фиксёрами Japantown.", "Может запускать квесты, связанные с контрабандой и расследованиями NetWatch."]}, "content": {"sections": [{"id": "catalog", "title": "Каталог и источники", "body": "Поддерживает коллекцию легальных шоу, экстремальных записей и экспериментальных сенсорных лент. Имеет контакты\\nс подпольными студиями и поставщиками из Pacifica. Следит за модой на новые жанры BD.\\n", "mechanics_links": ["mechanics/social/player-orders-via-npc.yaml"], "assets": []}, {"id": "services", "title": "Услуги и сделки", "body": "- Продажа стандартных, редких и эксклюзивных BD.\\n- Организация приватных просмотров и клубных мероприятий.\\n- Покупка сырья для студий, обмен лентами.\\n- Предупреждения о рейдах NetWatch за процент от сделки.\\n", "mechanics_links": ["mechanics/economy/economy-trading-guilds.yaml"], "assets": []}, {"id": "dialogue", "title": "Речевые сигнатуры", "body": "Фраза для привлечения клиента: «Ищешь острые ощущения — у меня есть.» Дилер оценивает запрос игрока и предлагает\\nбезопасный или экстремальный вариант, предупреждая о рисках.\\n", "mechanics_links": [], "assets": []}, {"id": "hooks", "title": "Наративные хуки", "body": "- Квесты по доставке запрещённых лент.\\n- Расследование исчезновения клиентов после просмотра экстремального BD.\\n- Сотрудничество с фиксёрами для записи новых сенсорных треков.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль BD-дилера Japantown в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

