INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-petrochem-director-refining-2035-2055', 'Director Refining Operations PetroChem (2035-2055)', '{"metadata": {"id": "npc-petrochem-director-refining-2035-2055", "title": "Director Refining Operations PetroChem (2035-2055)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "petrochem", "infrastructure"], "topics": ["refining-operations", "fuel-logistics"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [{"id": "scenarios-factions-corporations-petrochem-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/corporations/petrochem/director-refining-operations-2035-2055.md", "visibility": "internal", "audience": ["concept", "narrative", "operations"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль директора переработки PetroChem находился в Markdown и не подключался к линиям топливной логистики.", "goal": "Структурировать фигуру Хавьера Мендосы для квестов конвоев, ремонтов и корпоративных сделок.", "essence": "Инженер-антрикризисник удерживает топливную инфраструктуру куполов, балансируя между безопасностью и политикой.", "key_points": ["Снизил аварийность на 30% и оптимизировал поставки топлива.", "Заключил сделки с Biotechnica и обеспечил охрану конвоев через Arasaka и 6th Street.", "Стремится внедрить стандарты топлива Gen-2 и предотвратить технокатастрофы."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Хавьер Мендоса руководит перерабатывающими терминалами PetroChem в Техасе и Найт-Сити. Промышленный корпо-стиль\\nи импланты Enviro Filters подчёркивают акцент на безопасности и эффективности.\\n", "mechanics_links": ["mechanics/economy/economy-logistics.yaml"], "assets": []}, {"id": "operations", "title": "Операции и достижения", "body": "Ввёл антикризисные протоколы поставок, добился снижения аварийности на 30% и настроил обмен данными с Biotechnica\\nдля контроля качества топлива.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и цели", "body": "Стремится к бесперебойной работе инфраструктуры в течение 5 лет без серьёзных аварий. Боится техногенных\\nкатастроф и политических блокад, продвигает стандарты топлива Gen-2.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и партнёрства", "body": "Сотрудничает с Biotechnica в биотопливе, заключает охранные контракты с Arasaka и 6th Street, чтобы конвои\\nPetroChem доходили до куполов без атак.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровая роль", "body": "Предлагает квесты по защите конвоев, ремонту инфраструктуры и торговым переговорам. Навыки: технические —\\nвысокие, социальные — средние, боевые — низкие.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- Защита топливных колонн от банд и саботажа.\\n- Срыв конкурентов, пытающихся захватить терминалы.\\n- Решения о приоритете поставок между районами куполов.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль директора переработки PetroChem в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

