INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('common-trader-med-2030s-watson-001', 'Продавец медснабжения (Watson, 2030-е)', '{"metadata": {"id": "common-trader-med-2030s-watson-001", "title": "Продавец медснабжения (Watson, 2030-е)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "trader", "watson", "medical"], "topics": ["medical_supplies", "clinic_support"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [{"id": "mechanics/economy/economy-contracts", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/traders/med-supplies-2030s-watson.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Продавец медснабжения Watson был зафиксирован в Markdown и не использовался в сюжетах уличной медицины.", "goal": "Структурировать информацию о торговце расходниками для клиник и риппердоков Watson.", "essence": "Продавец обеспечивает бинты, стимуляторы и расходные материалы для уличных клиник, вовлечённых в локальную экономику.", "key_points": ["Снабжает риппердоков и медтехов базовыми расходниками.", "Предлагает стимуляторы и аптечки для игроков и NPC.", "Может запускать контракты по доставке редких медикаментов."]}, "content": {"sections": [{"id": "stock", "title": "Ассортимент", "body": "На складе присутствуют бинты, антисептики, одноразовые импланты, расходники для Trauma Team и бюджетных клиник.\\nПродавец регулярно обновляет запасы через связи с корпорациями и подпольными лабораториями.\\n", "mechanics_links": ["mechanics/economy/economy-logistics.yaml"], "assets": []}, {"id": "services", "title": "Услуги", "body": "- Продажа аптечек и стимуляторов разной степени эффективности.\\n- Комплектация заказов для клиник и риппердоков.\\n- Обмен редких компонентов на скидки и бонусы.\\n- Консультации по хранению медпрепаратов.\\n", "mechanics_links": ["mechanics/economy/economy-contracts.yaml"], "assets": []}, {"id": "dialogue", "title": "Речевые сигнатуры", "body": "Спокойный и уверенный тон: «Чистые руки — половина лечения.» Делится советами по применению товаров, но\\nосторожен с покупателями без репутации.\\n", "mechanics_links": [], "assets": []}, {"id": "hooks", "title": "Наративные хуки", "body": "- Сюжеты по поставке медикаментов в занятые районы.\\n- Квесты на поиск редких стимуляторов эмоционального восстановления.\\n- Сотрудничество с медтехами для стабилизации пациентов.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль продавца медснабжения Watson в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

