INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-common-trader-scrap-2040s-sd', 'NPC: Торговец металлоломом — Santo Domingo, 2040-е', '{"metadata": {"id": "npc-common-trader-scrap-2040s-sd", "title": "NPC: Торговец металлоломом — Santo Domingo, 2040-е", "document_type": "canon", "category": "npc-lore/common", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-06T08:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "santo-domingo", "trader", "scrap"], "topics": ["reconstruction-era", "salvage"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/traders/scrap-dealer-2040s-santo-domingo.md", "visibility": "internal", "audience": ["concept", "narrative", "economy"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Фигура торговца металлоломом существовала в Markdown и не была привязана к экономическим сценариям эпохи восстановления.", "goal": "Описать поставщика металлолома в унифицированной схеме знаний для использования в событиях дефицита ресурсов.", "essence": "Независимый торговец из Santo Domingo скупает металлолом и редкие детали, обеспечивая стройки и ремонтников.", "key_points": ["Работает в условиях дефицита и знает всех местных мародёров.", "Торгуется за каждую сделку и предоставляет информацию о ценах ресурсов.", "Служит источником материалов для строительных и ремонтных цепочек."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Торговец представляет эпоху Красного Времени: разрушенные кварталы и высокий спрос на металлолом. Он формирует\\nсеть поставщиков из мародёров, рабочих и строителей.\\n", "mechanics_links": [], "assets": []}, {"id": "inventory", "title": "Ассортимент", "body": "Основные товары — металлолом, старые запчасти, строительные материалы и инструменты. Наличие зависит от недавних\\nпоставок, а редкие детали появляются через события.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровое использование", "body": "Персонаж поддерживает ambient-сцены и обмен ресурсами, используется в квестах на восстановление районов и в\\nслучайных встречах с дефицитом деталей.\\n", "mechanics_links": [], "assets": []}, {"id": "behavior", "title": "Поведение", "body": "Ценит каждую сделку и активно торгуется. Может предоставить игрокам инсайды о местонахождении залежей металла и\\nпредупредить о конкурирующих сборщиках.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Перенесён профиль торговца металлоломом в YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

