INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('common-trader-weapon-2070s-watson-001', 'Торговец оружием (Watson, 2070-е)', '{"metadata": {"id": "common-trader-weapon-2070s-watson-001", "title": "Торговец оружием (Watson, 2070-е)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "trader", "watson", "weapons"], "topics": ["arms_market", "modding"], "related_systems": ["narrative-service", "combat-service"], "related_documents": [{"id": "mechanics/combat/combat-weapon-classes.yaml", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/traders/weapon-dealer-2070s-watson.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Торговец оружием Watson был записан в Markdown и отсутствовал в базе знаний.", "goal": "Стандартизировать профиль оружейного дилера для боевых и экономических сценариев.", "essence": "Дилер продаёт огнестрел, модификации и выкупает трофеи, формируя локальный рынок вооружения.", "key_points": ["Предлагает широкий выбор оружия и модов.", "Работает с фиксёрскими контрактами и проверяет репутацию клиентов.", "Может запускать задания на поиск или тестирование новых образцов."]}, "content": {"sections": [{"id": "arsenal", "title": "Ассортимент", "body": "- Пистолеты, штурмовые винтовки, дробовики 2070-х годов.\\n- Моды: компенсаторы, прицелы, интеллектуальные чипы.\\n- Редкие образцы от корпораций Arasaka и Militech под заказ.\\n", "mechanics_links": ["mechanics/combat/combat-weapon-classes.yaml"], "assets": []}, {"id": "services", "title": "Услуги", "body": "- Продажа и выкуп оружия с проверкой происхождения.\\n- Настройка модификаций и тестирование баланса.\\n- Комиссионные сделки для фиксёров и клиентов с репутацией.\\n", "mechanics_links": ["mechanics/economy/economy-trading-guilds.yaml"], "assets": []}, {"id": "dialogue", "title": "Речь и стиль", "body": "Подчёркнуто деловой тон: «Есть вопросы по калибрам — задавай.» Поощряет информированных клиентов и напоминает\\nо последствиях неправильной настройки модов.\\n", "mechanics_links": [], "assets": []}, {"id": "hooks", "title": "Наративные хуки", "body": "- Квесты по доставке вооружения в горячие точки.\\n- Сбор редких компонентов для экспериментальных модов.\\n- Линии доверия, открывающие эксклюзивные модели оружия.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль торговца оружием Watson в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

