INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-6th-community-judge-2065-2075', 'Судья общин 6th Street (2065-2075)', '{"metadata": {"id": "npc-6th-community-judge-2065-2075", "title": "Судья общин 6th Street (2065-2075)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "6th-street", "community-justice"], "topics": ["restorative-justice", "neighborhood-governance"], "related_systems": ["narrative-service", "social-system"], "related_documents": [{"id": "scenarios-factions-gangs-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/gangs/6th-street/community-judge-2065-2075.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Судья общин 6th Street был оформлен в Markdown и не подключен к системам правосудия и медиации кварталов.", "goal": "Описать Линду Пайк как арбитра конфликтов, задающего стандарты «уличной хартии».", "essence": "Юрист по образованию формирует параправовую систему 6th Street, обеспечивая предсказуемые решения и штрафы.", "key_points": ["Разрабатывает кодекс «6th Street 2.0» и внедряет прозрачные процедуры.", "Ведёт диалог с бандами и жителями для урегулирования конфликтов.", "Служит точкой входа в квесты медиации, штрафов и помилований."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Линда Пайк — бывший адвокат, ставшая общинным судьёй 6th Street. Она устраивает открытые слушания, фиксирует\\nштрафы и медиирует споры между жителями и дружинниками. Её подход минималистичен и лишён показной роскоши.\\n", "mechanics_links": ["mechanics/social/relationships-system.yaml"], "assets": []}, {"id": "justice_system", "title": "Система уличной справедливости", "body": "Разработала «уличную хартию» с фиксированными штрафами, компенсациями и программами исправления. Её решения\\nпубликуются в сетевых каналах квартала и служат эталоном для других дружин.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и цели", "body": "Стремится к предсказуемости и доверию. Боится, что радикальные крылья захватят суд и превратят его в инструмент\\nрепрессий. Хочет признания со стороны соседних кварталов.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи", "body": "Диалог с Valentinos ради снижения насилия, напряжённые отношения с NCPD из-за параллельной юрисдикции, избегает\\nкорпораций и фиксирует их нарушения в отчётах.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровая роль", "body": "Предлагает игроку квесты медиации, снятия штрафов и защиты свидетелей. Навыки: социальные — высокие, аналитические\\n— средние, боевые — низкие. Репутация открывает возможность влиять на кодекс.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- Рассмотрение конфликта между дружинниками и жителями.\\n- Расследование попыток коррупции со стороны NCPD.\\n- Расширение практик медиации на другие районы города.\\n", "mechanics_links": ["mechanics/economy/economy-contracts.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль судьи общин 6th Street в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

